import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2956.1 Powers of Board concerning occupational therapy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2956.1.md","filePath":"vacode/54.1-2956.1.md"}'),o={name:"vacode/54.1-2956.1.md"},n=r('<h1 id="§-54-1-2956-1-powers-of-board-concerning-occupational-therapy" tabindex="-1">§ 54.1-2956.1 Powers of Board concerning occupational therapy. <a class="header-anchor" href="#§-54-1-2956-1-powers-of-board-concerning-occupational-therapy" aria-label="Permalink to &quot;§ 54.1-2956.1 Powers of Board concerning occupational therapy.&quot;">​</a></h1><p>The Board shall take such actions as may be necessary to ensure the competence and integrity of any person who practices occupational therapy or claims to be an occupational therapist or occupational therapy assistant or who holds himself out to the public as an occupational therapist or occupational therapy assistant or who engages in the practice of occupational therapy, and to that end it may license practitioners as occupational therapists or occupational therapy assistants who have met the qualifications established in regulation by the Board.</p><p>The Board shall consider and may accept relevant practical experience and didactic and clinical components of education and training completed by an applicant for licensure as an occupational therapist during his service as a member of any branch of the armed forces of the United States as evidence of the satisfaction of the educational requirements for licensure as an occupational therapist.</p><p>1989, c. 306; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0593" target="_blank" rel="noreferrer">593</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0782" target="_blank" rel="noreferrer">782</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>.</p>',4),i=[n];function c(s,l,p,h,d,f){return a(),t("div",null,i)}const _=e(o,[["render",c]]);export{u as __pageData,_ as default};
