import{_ as s,o as a,c as e,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 54.1-2956.13 Licensure of surgical assistant; practice of surgical assisting; use of title.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2956.13.md","filePath":"vacode/54.1-2956.13.md"}'),t={name:"vacode/54.1-2956.13.md"},r=i('<h1 id="§-54-1-2956-13-licensure-of-surgical-assistant-practice-of-surgical-assisting-use-of-title" tabindex="-1">§ 54.1-2956.13 Licensure of surgical assistant; practice of surgical assisting; use of title. <a class="header-anchor" href="#§-54-1-2956-13-licensure-of-surgical-assistant-practice-of-surgical-assisting-use-of-title" aria-label="Permalink to &quot;§ 54.1-2956.13 Licensure of surgical assistant; practice of surgical assisting; use of title.&quot;">​</a></h1><p>A. No person shall engage in the practice of surgical assisting or use or assume the title &quot;surgical assistant&quot; unless such person holds a license as a surgical assistant issued by the Board. Nothing in this section shall be construed as prohibiting any professional licensed, certified, or registered by a health regulatory board from acting within the scope of his practice.</p><p>B. The Board shall establish criteria for licensure as a surgical assistant, which shall include evidence that the applicant:</p><ol><li><p>Holds a current credential as a surgical assistant or surgical first assistant issued by the National Board of Surgical Technology and Surgical Assisting, or the National Commission for Certification of Surgical Assistants or their successors;</p></li><li><p>Has successfully completed a surgical assistant training program during the person&#39;s service as a member of any branch of the armed forces of the United States; or</p></li><li><p>Has practiced as a surgical assistant in the Commonwealth at any time in the six months immediately prior to July 1, 2020.</p></li></ol><p>C. For renewal of a license, a surgical assistant who was licensed based on a credential as a surgical assistant or surgical first assistant issued by the National Board of Surgical Technology and Surgical Assisting, or the National Commission for the Certification of Surgical Assistants or their successors shall attest that the credential is current at the time of renewal.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0531" target="_blank" rel="noreferrer">531</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0099" target="_blank" rel="noreferrer">99</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0374" target="_blank" rel="noreferrer">374</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1222" target="_blank" rel="noreferrer">1222</a>.</p>',6),o=[r];function n(l,c,u,g,h,f){return a(),e("div",null,o)}const _=s(t,[["render",n]]);export{d as __pageData,_ as default};
