import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-2956.4 Advisory Board of Occupational Therapy; powers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2956.4.md","filePath":"vacode/54.1-2956.4.md"}'),t={name:"vacode/54.1-2956.4.md"},i=r('<h1 id="§-54-1-2956-4-advisory-board-of-occupational-therapy-powers" tabindex="-1">§ 54.1-2956.4 Advisory Board of Occupational Therapy; powers. <a class="header-anchor" href="#§-54-1-2956-4-advisory-board-of-occupational-therapy-powers" aria-label="Permalink to &quot;§ 54.1-2956.4 Advisory Board of Occupational Therapy; powers.&quot;">​</a></h1><p>The Advisory Board shall, under the authority of the Board:</p><ol><li><p>Recommend to the Board, for its promulgation into regulation, the criteria for licensure as an occupational therapist or an occupational therapy assistant and the standards of professional conduct for holders of licenses.</p></li><li><p>Assess the qualifications of applicants for licensure and recommend licensure when applicants meet the required criteria. The recommendations of the Advisory Board on licensure of applicants shall be presented to the Board, which shall then issue or deny licenses. Any applicant who is aggrieved by a denial of recommendation on licensure of the Advisory Board may appeal to the Board.</p></li><li><p>Receive investigative reports of professional misconduct and unlawful acts and recommend sanctions when appropriate. Any recommendation of sanctions shall be presented to the Board, which may then impose sanctions or take such other action as may be warranted by law.</p></li><li><p>Assist in such other matters dealing with occupational therapy as the Board may in its discretion direct.</p></li></ol><p>1989, c. 306; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0593" target="_blank" rel="noreferrer">593</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>.</p>',4),n=[i];function s(c,l,p,d,h,f){return a(),o("div",null,n)}const m=e(t,[["render",s]]);export{_ as __pageData,m as default};
