import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2956.5 Unlawful to practice occupational therapy without license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2956.5.md","filePath":"vacode/54.1-2956.5.md"}'),r={name:"vacode/54.1-2956.5.md"},i=o('<h1 id="§-54-1-2956-5-unlawful-to-practice-occupational-therapy-without-license" tabindex="-1">§ 54.1-2956.5 Unlawful to practice occupational therapy without license. <a class="header-anchor" href="#§-54-1-2956-5-unlawful-to-practice-occupational-therapy-without-license" aria-label="Permalink to &quot;§ 54.1-2956.5 Unlawful to practice occupational therapy without license.&quot;">​</a></h1><p>A. It shall be unlawful for any person not holding a current and valid license from the Board to practice occupational therapy or to claim to be an occupational therapist or to assume the title &quot;Occupational Therapist,&quot; &quot;Occupational Therapist, Licensed,&quot; &quot;Licensed Occupational Therapist,&quot; or any similar term, or to use the designations &quot;O.T.&quot; or &quot;O.T.L.&quot; or any variation thereof. However, a person who has graduated from a duly accredited educational program in occupational therapy may practice with the title &quot;Occupational Therapist, License Applicant&quot; or &quot;O.T.L.-Applicant&quot; until he has received a failing score on any examination required by the Board or until six months from the date of graduation, whichever occurs sooner.</p><p>B. It shall be unlawful for any person to practice as an occupational therapy assistant as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-2900/" target="_blank" rel="noreferrer">54.1-2900</a> or to hold himself out to be or advertise that he is an occupational therapy assistant or use the designation &quot;O.T.A.&quot; or any variation thereof unless such person holds a current and valid license from the Board to practice as an occupational therapy assistant. However, a person who has graduated from a duly accredited occupational therapy assistant education program may practice with the title &quot;Occupational Therapy Assistant, License Applicant&quot; or &quot;O.T.A.-Applicant&quot; until he has received a failing score on any examination required by the Board or until six months from the date of graduation, whichever occurs sooner.</p><p>1989, c. 306; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0593" target="_blank" rel="noreferrer">593</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0782" target="_blank" rel="noreferrer">782</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0252" target="_blank" rel="noreferrer">252</a>.</p>',4),n=[i];function c(l,s,u,p,h,d){return a(),t("div",null,n)}const _=e(r,[["render",c]]);export{g as __pageData,_ as default};
