import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2957.20 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2957.20.md","filePath":"vacode/54.1-2957.20.md"}'),n={name:"vacode/54.1-2957.20.md"},a=o('<h1 id="§-54-1-2957-20-exemptions" tabindex="-1">§ 54.1-2957.20 Exemptions. <a class="header-anchor" href="#§-54-1-2957-20-exemptions" aria-label="Permalink to &quot;§ 54.1-2957.20 Exemptions.&quot;">​</a></h1><p>The provisions of this chapter shall not prohibit:</p><ol><li><p>A licensed and qualified health care provider from practicing within his scope of practice, provided he does not use the title &quot;genetic counselor&quot; or any other title tending to indicate he is a genetic counselor unless licensed in the Commonwealth;</p></li><li><p>A student from performing genetic counseling as part of an approved academic program in genetic counseling, provided he is supervised by a licensed genetic counselor and designated by a title clearly indicating his status as a student or trainee; or</p></li><li><p>A person who holds a current, valid certificate issued by the American Board of Genetic Counseling or American Board of Medical Genetics to practice genetic counseling, who is employed by a rare disease organization located in another jurisdiction, and who complies with the licensure requirements of that jurisdiction from providing genetic counseling in the Commonwealth fewer than 10 days per year.</p></li></ol><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0010" target="_blank" rel="noreferrer">10</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>.</p>',4),r=[a];function s(c,l,d,p,h,u){return i(),t("div",null,r)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
