import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2957.5 Advisory Board on Athletic Training established; duties; composition; appointment; terms.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2957.5.md","filePath":"vacode/54.1-2957.5.md"}'),a={name:"vacode/54.1-2957.5.md"},o=i('<h1 id="§-54-1-2957-5-advisory-board-on-athletic-training-established-duties-composition-appointment-terms" tabindex="-1">§ 54.1-2957.5 Advisory Board on Athletic Training established; duties; composition; appointment; terms. <a class="header-anchor" href="#§-54-1-2957-5-advisory-board-on-athletic-training-established-duties-composition-appointment-terms" aria-label="Permalink to &quot;§ 54.1-2957.5 Advisory Board on Athletic Training established; duties; composition; appointment; terms.&quot;">​</a></h1><p>A. The Advisory Board on Athletic Training shall assist the Board in formulating its requirements for the licensure of athletic trainers. In the exercise of this responsibility, the Advisory Board shall recommend to the Board the criteria for licensure of athletic trainers and the standards of professional conduct for licensees. The Advisory Board shall also assist in such other matters relating to the practice of athletic training as the Board may require.</p><p>B. The Advisory Board shall consist of five members appointed by the Governor for four-year terms. The first appointments shall provide for staggered terms with two members being appointed for a two-year term, two members being appointed for a three-year term and one member being appointed for a four-year term. Three members shall be at the time of appointment athletic trainers who are currently licensed by the Board and who have practiced in Virginia for not less than three years, including one athletic trainer employed at a secondary school, one employed at an institution of higher education, and one employed in the public or private sector; one member shall be a physician licensed to practice medicine in the Commonwealth; and one member shall be a citizen appointed by the Governor from the Commonwealth at large.</p><p>Vacancies occurring other than by expiration of term shall be filled for the unexpired term. No person shall be eligible to serve on the Advisory Board for more than two full consecutive terms.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0639" target="_blank" rel="noreferrer">639</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0669" target="_blank" rel="noreferrer">669</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0714" target="_blank" rel="noreferrer">714</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0926" target="_blank" rel="noreferrer">926</a>.</p>',5),n=[o];function s(l,h,c,d,p,m){return t(),r("div",null,n)}const b=e(a,[["render",s]]);export{g as __pageData,b as default};
