import{_ as i,o as a,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-2964 Disclosure of interest in referral facilities and clinical laboratories.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2964.md","filePath":"vacode/54.1-2964.md"}'),o={name:"vacode/54.1-2964.md"},r=e("h1",{id:"§-54-1-2964-disclosure-of-interest-in-referral-facilities-and-clinical-laboratories",tabindex:"-1"},[t("§ 54.1-2964 Disclosure of interest in referral facilities and clinical laboratories. "),e("a",{class:"header-anchor",href:"#§-54-1-2964-disclosure-of-interest-in-referral-facilities-and-clinical-laboratories","aria-label":'Permalink to "§ 54.1-2964 Disclosure of interest in referral facilities and clinical laboratories."'},"​")],-1),s=e("p",null,[t("A. Any practitioner of the healing arts shall, prior to referral of a patient to any facility or entity engaged in the provision of health-related services, appliances or devices, including but not limited to physical therapy, hearing testing, or sale or fitting of hearing aids or eyeglasses provide the patient with a notice in bold print that discloses any known material financial interest of or ownership by the practitioner in such facility or entity and states that the services, appliances or devices may be available from other suppliers in the community. In making any such referral, the practitioner of the healing arts may render such recommendations as he considers appropriate, but shall advise the patient of his freedom of choice in the selection of such facility or entity. This section shall not be construed to permit any of the practices prohibited in § "),e("a",{href:"http://law.lis.virginia.gov/vacode/54.1-2914/",target:"_blank",rel:"noreferrer"},"54.1-2914"),t(" or Chapter 24.1 (§ "),e("a",{href:"http://law.lis.virginia.gov/vacode/54.1-2410/",target:"_blank",rel:"noreferrer"},"54.1-2410"),t(" et seq.) of this title.")],-1),c=e("p",null,"In addition, any practitioner of the healing arts shall, prior to ordering any medical test from an independent clinical laboratory for a patient, provide the patient with notice in bold print that discloses any known material financial interest or ownership by the practitioner in such laboratory unless the independent clinical laboratory is operated by a publicly held corporation. The practitioner shall inform the patient about the accreditation status and credentials of the laboratory.",-1),l=e("p",null,"B. The Attorney General, an attorney of the Commonwealth, the attorney for a city, county or town, or any aggrieved patient may cause an action to be brought in the appropriate circuit court in the name of the Commonwealth, of the county, city or town, or of any aggrieved patient, to enjoin any violation of this section. The circuit court having jurisdiction may enjoin such violations, notwithstanding the existence of an adequate remedy at law. When an injunction is issued, the circuit court may impose a civil fine to be paid to the Literary Fund not to exceed $1,000. In any action under this section, it shall not be necessary that damages be proven.",-1),h=e("p",null,"1986, c. 348, § 54-278.3; 1988, cc. 765, 874; 1989, c. 282; 1993, c. 869.",-1),d=[r,s,c,l,h];function p(f,u,y,m,b,g){return a(),n("div",null,d)}const w=i(o,[["render",p]]);export{_ as __pageData,w as default};
