import{_ as e,o as i,c as a,O as s}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2971.01 Prescription in excess of recommended dosage in certain cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2971.01.md","filePath":"vacode/54.1-2971.01.md"}'),r={name:"vacode/54.1-2971.01.md"},o=s('<h1 id="§-54-1-2971-01-prescription-in-excess-of-recommended-dosage-in-certain-cases" tabindex="-1">§ 54.1-2971.01 Prescription in excess of recommended dosage in certain cases. <a class="header-anchor" href="#§-54-1-2971-01-prescription-in-excess-of-recommended-dosage-in-certain-cases" aria-label="Permalink to &quot;§ 54.1-2971.01 Prescription in excess of recommended dosage in certain cases.&quot;">​</a></h1><p>A. Consistent with § <a href="http://law.lis.virginia.gov/vacode/54.1-3408.1/" target="_blank" rel="noreferrer">54.1-3408.1</a>, a physician may prescribe a dosage of a pain-relieving agent in excess of the recommended dosage upon certifying the medical necessity for the excess dosage in the patient&#39;s medical record. Any practitioner who prescribes, dispenses or administers an excess dosage in accordance with this section and § <a href="http://law.lis.virginia.gov/vacode/54.1-3408.1/" target="_blank" rel="noreferrer">54.1-3408.1</a> shall not be in violation of the provisions of this title because of such excess dosage, if such excess dosage is prescribed, dispensed or administered in good faith for recognized medicinal or therapeutic purposes.</p><p>B. The Board of Medicine shall advise physicians of the provisions of this section and § <a href="http://law.lis.virginia.gov/vacode/54.1-3408.1/" target="_blank" rel="noreferrer">54.1-3408.1</a>.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0277" target="_blank" rel="noreferrer">277</a>.</p>',4),n=[o];function t(c,d,p,h,l,f){return i(),a("div",null,n)}const m=e(r,[["render",t]]);export{g as __pageData,m as default};
