import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-2985.1 Injunction; court-ordered health care.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2985.1.md","filePath":"vacode/54.1-2985.1.md"}'),o={name:"vacode/54.1-2985.1.md"},n=a('<h1 id="§-54-1-2985-1-injunction-court-ordered-health-care" tabindex="-1">§ 54.1-2985.1 Injunction; court-ordered health care. <a class="header-anchor" href="#§-54-1-2985-1-injunction-court-ordered-health-care" aria-label="Permalink to &quot;§ 54.1-2985.1 Injunction; court-ordered health care.&quot;">​</a></h1><p>A. On petition of any person to the circuit court of the county or city in which any patient resides or is located for whom health care will be or is currently being provided, continued, withheld, or withdrawn pursuant to this article, the court may enjoin such action upon finding by a preponderance of the evidence that the action is not lawfully authorized by this article or by other state or federal law.</p><p>B. Nothing in this article shall limit the ability of any person to petition and obtain a court order for health care, including mental health treatment authorized by Chapter 8 (§ <a href="http://law.lis.virginia.gov/vacode/37.2-800/" target="_blank" rel="noreferrer">37.2-800</a> et seq.) of Title 37.2, of any patient pursuant to any other existing law in the Commonwealth.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0211" target="_blank" rel="noreferrer">211</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0268" target="_blank" rel="noreferrer">268</a>.</p>',4),i=[n];function c(h,l,d,s,u,p){return t(),r("div",null,i)}const g=e(o,[["render",c]]);export{f as __pageData,g as default};
