import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 54.1-3002 Board of Nursing; membership; terms; meetings; quorum; administrative officer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3002.md","filePath":"vacode/54.1-3002.md"}'),i={name:"vacode/54.1-3002.md"},s=a('<h1 id="§-54-1-3002-board-of-nursing-membership-terms-meetings-quorum-administrative-officer" tabindex="-1">§ 54.1-3002 Board of Nursing; membership; terms; meetings; quorum; administrative officer. <a class="header-anchor" href="#§-54-1-3002-board-of-nursing-membership-terms-meetings-quorum-administrative-officer" aria-label="Permalink to &quot;§ 54.1-3002 Board of Nursing; membership; terms; meetings; quorum; administrative officer.&quot;">​</a></h1><p>The Board of Nursing shall consist of 14 members as follows: eight registered nurses, at least two of whom are licensed nurse practitioners; two licensed practical nurses; three citizen members; and one member who shall be a registered nurse or a licensed practical nurse. The terms of office of the Board shall be four years.</p><p>The Board shall meet at least annually and shall elect officers from its membership. It may hold such other meetings as may be necessary to perform its duties. A majority of the Board including one of its officers shall constitute a quorum for the conduct of business at any meeting. Special meetings of the Board shall be called by the administrative officer upon written request of two members.</p><p>The Board shall have an administrative officer who shall be a registered nurse.</p><p>Code 1950, §§ 54-327, 54-328, 54-336, 54-341, 54-342; 1960, c. 61; 1968, c. 337, § 54-367.12; 1970, c. 116, § 54-367.10; 1975, c. 106, §§ 54-367.3, 54-367.4; 1979, c. 5; 1980, c. 728; 1982, c. 598; 1986, c. 464; 1988, c. 765; 1993, c. 404; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0034" target="_blank" rel="noreferrer">34</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0105" target="_blank" rel="noreferrer">105</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0169" target="_blank" rel="noreferrer">169</a>.</p>',5),o=[s];function n(c,m,l,f,d,h){return r(),t("div",null,o)}const g=e(i,[["render",n]]);export{p as __pageData,g as default};
