import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3007 Refusal, revocation or suspension, censure or probation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3007.md","filePath":"vacode/54.1-3007.md"}'),o={name:"vacode/54.1-3007.md"},a=t('<h1 id="§-54-1-3007-refusal-revocation-or-suspension-censure-or-probation" tabindex="-1">§ 54.1-3007 Refusal, revocation or suspension, censure or probation. <a class="header-anchor" href="#§-54-1-3007-refusal-revocation-or-suspension-censure-or-probation" aria-label="Permalink to &quot;§ 54.1-3007 Refusal, revocation or suspension, censure or probation.&quot;">​</a></h1><p>The Board may refuse to admit a candidate to any examination, refuse to issue a license, certificate, or registration to any applicant and may suspend any license, certificate, registration, or multistate licensure privilege for a stated period or indefinitely, or revoke any license, certificate, registration, or multistate licensure privilege, or censure or reprimand any licensee, certificate holder, registrant, or multistate licensure privilege holder, or place him on probation for such time as it may designate for any of the following causes:</p><ol><li><p>Fraud or deceit in procuring or attempting to procure a license, certificate, or registration;</p></li><li><p>Unprofessional conduct;</p></li><li><p>Willful or repeated violation of any of the provisions of this chapter;</p></li><li><p>Conviction of any felony or any misdemeanor involving moral turpitude;</p></li><li><p>Practicing in a manner contrary to the standards of ethics or in such a manner as to make his practice a danger to the health and welfare of patients or to the public;</p></li><li><p>Use of alcohol or drugs to the extent that such use renders him unsafe to practice, or any mental or physical illness rendering him unsafe to practice;</p></li><li><p>The denial, revocation, suspension or restriction of a license, certificate, registration, or multistate licensure privilege to practice in another state, the District of Columbia or a United States possession or territory; or</p></li><li><p>Abuse, negligent practice, or misappropriation of a patient&#39;s or resident&#39;s property.</p></li></ol><p>Code 1950, § 54-353; 1970, c. 116; 1979, c. 5, § 54-367.32; 1982, c. 598; 1988, c. 765; 1989, c. 278; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0249" target="_blank" rel="noreferrer">249</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0610" target="_blank" rel="noreferrer">610</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0924" target="_blank" rel="noreferrer">924</a>.</p>',4),n=[a];function s(c,l,p,u,f,d){return r(),i("div",null,n)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
