import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3011.2 Nursing Scholarship and Loan Repayment Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3011.2.md","filePath":"vacode/54.1-3011.2.md"}'),n={name:"vacode/54.1-3011.2.md"},o=t('<h1 id="§-54-1-3011-2-nursing-scholarship-and-loan-repayment-fund" tabindex="-1">§ 54.1-3011.2 Nursing Scholarship and Loan Repayment Fund. <a class="header-anchor" href="#§-54-1-3011-2-nursing-scholarship-and-loan-repayment-fund" aria-label="Permalink to &quot;§ 54.1-3011.2 Nursing Scholarship and Loan Repayment Fund.&quot;">​</a></h1><p>A. There is hereby established the Nursing Scholarship and Loan Repayment Fund for the purpose of financing scholarships for (i) part-time and full-time students enrolled in or accepted for enrollment by nursing programs which will prepare such students, upon completion, for examination to be licensed by the Board as practical nurses or registered nurses and (ii) those registered nurses, licensed practical nurses, and certified nurse aides who agree to perform a period of nursing service in a Commonwealth long-term care facility pursuant to regulations promulgated by the Board of Health in cooperation with the Board.</p><p>B. The Fund shall be administered by the Board, in cooperation with the Director of the Department, and the scholarships shall be administered and awarded by the Board of Health pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-122.6:01/" target="_blank" rel="noreferrer">32.1-122.6:01</a>. The Fund shall be maintained and administered separately from any other program or funds of the Board and the Department of Health Professions. No portion of the Fund shall be used for a purpose other than that described in this section and § <a href="http://law.lis.virginia.gov/vacode/32.1-122.6:01/" target="_blank" rel="noreferrer">32.1-122.6:01</a>. Any money remaining in the Fund at the end of a biennium, including amounts repaid by award recipients, and any interest thereon, shall not revert to the general fund or the funds of the Department of Health Professions, but shall remain in the Fund to be used only for the purposes of this section. In addition to any licensure fees that may be collected pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-3011.1/" target="_blank" rel="noreferrer">54.1-3011.1</a>, the Fund shall also include:</p><ol><li><p>Any funds appropriated by the General Assembly for the purposes of the Fund; and</p></li><li><p>Any gifts, grants, or bequests received from any private person or organization.</p></li></ol><p>Upon receiving the names of the scholarship and loan repayment program recipients from the Board of Health, the Board of Nursing shall be responsible for transmitting the funds to the appropriate institution to be credited to the account of the recipient.</p><p>1991, c. 669; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0240" target="_blank" rel="noreferrer">240</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0254" target="_blank" rel="noreferrer">254</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0188" target="_blank" rel="noreferrer">188</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0290" target="_blank" rel="noreferrer">290</a>.</p>',6),i=[o];function s(l,h,d,p,c,f){return r(),a("div",null,i)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
