import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-3013 Approval of nursing education program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3013.md","filePath":"vacode/54.1-3013.md"}'),t={name:"vacode/54.1-3013.md"},i=o('<h1 id="§-54-1-3013-approval-of-nursing-education-program" tabindex="-1">§ 54.1-3013 Approval of nursing education program. <a class="header-anchor" href="#§-54-1-3013-approval-of-nursing-education-program" aria-label="Permalink to &quot;§ 54.1-3013 Approval of nursing education program.&quot;">​</a></h1><p>An institution desiring to conduct a nursing education program to prepare professional or practical nurses shall apply to the Board and submit evidence that:</p><ol><li><p>It is prepared to meet the minimum standards prescribed by the Board for either a professional nursing curriculum or a practical nursing curriculum; and</p></li><li><p>It is prepared to meet such other standards as may be established by law or by the Board.</p></li></ol><p>A survey of the institution and its entire nursing education program shall be made by the administrative officer or other authorized employee of the Board, who shall submit a written report of the survey to the Board. If, in the opinion of the Board, the requirements necessary for approval are met, it shall be approved as a nursing education program for professional or practical nurses.</p><p>New nursing education programs shall not be established or conducted unless approved by the Board.</p><p>Code 1950, §§ 54-355, 54-357; 1970, c. 116, §§ 54-367.27, 54-367.29; 1988, c. 765; 1989, c. 7; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>.</p>',6),n=[i];function s(p,d,c,l,u,h){return r(),a("div",null,n)}const _=e(t,[["render",s]]);export{f as __pageData,_ as default};
