import{_ as a,o as e,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 54.1-3020 Qualifications of applicant for practical nurse's license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3020.md","filePath":"vacode/54.1-3020.md"}`),c={name:"vacode/54.1-3020.md"},r=t('<h1 id="§-54-1-3020-qualifications-of-applicant-for-practical-nurse-s-license" tabindex="-1">§ 54.1-3020 Qualifications of applicant for practical nurse&#39;s license. <a class="header-anchor" href="#§-54-1-3020-qualifications-of-applicant-for-practical-nurse-s-license" aria-label="Permalink to &quot;§ 54.1-3020 Qualifications of applicant for practical nurse&#39;s license.&quot;">​</a></h1><p>A. An applicant for a license to practice as a practical nurse shall furnish evidence satisfactory to the Board that the applicant:</p><ol><li><p>Has completed two years of high school or its equivalent;</p></li><li><p>Has received a diploma from an approved practical nursing program;</p></li><li><p>Has passed a written examination as required by the Board; and</p></li><li><p>Has committed no acts which are grounds for disciplinary action as set forth in this chapter.</p></li></ol><p>B. The Board shall consider and may accept relevant practical experience and didactic and clinical components of education and training completed by an applicant for licensure as a practical nurse during his service as a member of any branch of the armed forces of the United States as evidence of the satisfaction of the educational requirements for licensure.</p><p>Code 1950, § 54-348; 1970, c. 116; 1979, c. 5, § 54-367.19; 1982, c. 598; 1988, c. 765; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>.</p>',5),n=[r];function o(s,l,p,d,f,h){return e(),i("div",null,n)}const m=a(c,[["render",o]]);export{_ as __pageData,m as default};
