import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-310.1 Petitions for regulation; review by Board; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-310.1.md","filePath":"vacode/54.1-310.1.md"}'),a={name:"vacode/54.1-310.1.md"},i=r('<h1 id="§-54-1-310-1-petitions-for-regulation-review-by-board-report" tabindex="-1">§ 54.1-310.1 Petitions for regulation; review by Board; report. <a class="header-anchor" href="#§-54-1-310-1-petitions-for-regulation-review-by-board-report" aria-label="Permalink to &quot;§ 54.1-310.1 Petitions for regulation; review by Board; report.&quot;">​</a></h1><p>A. Any professional or occupational group or organization, any person, or any other interested party that proposes the regulation of any unregulated professional or occupational group shall submit a request to the Board no later than December 1 of any year for analysis and evaluation during the following year.</p><p>B. The Board shall review the request only when filed with a statement of support for the proposed regulation signed by at least 10 members of the professional or occupational group for which regulation is being sought or at least 10 individuals who are not members of the professional or occupational group.</p><p>C. The request shall include, at a minimum, the following information:</p><ol><li><p>A description of the group proposed for regulation, including a list of associations, organizations, and other groups representing the practitioners in the Commonwealth, and an estimate of the number of practitioners in each group;</p></li><li><p>A definition of the problems to be solved by regulation and the reasons why regulation is necessary;</p></li><li><p>The reasons why registration, certification, licensure, or other type of regulation is being proposed and why that regulatory alternative was chosen;</p></li><li><p>The benefit to the public that would result from the proposed regulation;</p></li><li><p>The cost of the proposed regulation; and</p></li><li><p>A description of any anticipated disqualifications on an applicant for certification, licensure, or renewal and how such disqualifications serve public safety or commercial or consumer protection interests.</p></li></ol><p>D. Upon receipt of a request submitted in accordance with the requirements of subsection C, the Board shall conduct an analysis and evaluation of any proposed regulation based on the criteria enumerated in § <a href="http://law.lis.virginia.gov/vacode/54.1-311/" target="_blank" rel="noreferrer">54.1-311</a>.</p><p>E. The Board may decline to conduct a review only if it:</p><ol><li><p>Previously conducted an analysis and evaluation of the proposed regulation of the same professional or occupational group;</p></li><li><p>Issued a report not more than three years prior to the submission of the current proposal to regulate the same professional or occupational group; and</p></li><li><p>Finds that no new information has been submitted in the request that would cause the Board to alter or modify the recommendations made in its earlier report on the proposed regulation of the professional or occupational group.</p></li></ol><p>F. The Board shall submit a report with its findings on whether the public interest requires the requested professional or occupational group be regulated to the House Committee on General Laws, the Senate Committee on General Laws and Technology, and the Joint Commission on Administrative Rules no later than November 1 of the year following the request submission.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0467" target="_blank" rel="noreferrer">467</a>.</p>',10),n=[i];function s(l,p,u,h,c,d){return o(),t("div",null,n)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
