import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-3202 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3202.md","filePath":"vacode/54.1-3202.md"}'),r={name:"vacode/54.1-3202.md"},a=o('<h1 id="§-54-1-3202-exemptions" tabindex="-1">§ 54.1-3202 Exemptions. <a class="header-anchor" href="#§-54-1-3202-exemptions" aria-label="Permalink to &quot;§ 54.1-3202 Exemptions.&quot;">​</a></h1><p>This chapter shall not apply to:</p><ol><li><p>Physicians licensed to practice medicine by the Board of Medicine or to prohibit the sale of nonprescription eyeglasses and sunglasses; or</p></li><li><p>Any optometrist rendering free health care to an underserved population in Virginia who (i) does not regularly practice optometry in Virginia, (ii) holds a current valid license or certificate to practice optometry in another state, territory, district or possession of the United States, (iii) volunteers to provide free health care in an underserved area of this Commonwealth under the auspices of a publicly supported all volunteer, nonprofit organization that sponsors the provision of health care to populations of underserved people, (iv) files a copy of his license or certification in such other jurisdiction with the Board, (v) notifies the Board at least five business days prior to the voluntary provision of services of the dates and location of such service, and (vi) acknowledges, in writing, that such licensure exemption shall only be valid, in compliance with the Board&#39;s regulations, during the limited period that such free health care is made available through the volunteer, nonprofit organization on the dates and at the location filed with the Board. The Board may deny the right to practice in Virginia to any optometrist whose license or certificate has been previously suspended or revoked, who has been convicted of a felony or who is otherwise found to be in violation of applicable laws or regulations. However, the Board shall allow an optometrist who meets the above criteria to provide volunteer services without prior notice for a period of up to three days, provided the nonprofit organization verifies that the practitioner has a valid, unrestricted license in another state.</p></li></ol><p>Code 1950, § 54-369; 1988, c. 765; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>.</p>',4),n=[a];function s(l,c,h,p,d,f){return t(),i("div",null,n)}const g=e(r,[["render",s]]);export{v as __pageData,g as default};
