import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-3207 Board of Optometry.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3207.md","filePath":"vacode/54.1-3207.md"}'),r={name:"vacode/54.1-3207.md"},i=o('<h1 id="§-54-1-3207-board-of-optometry" tabindex="-1">§ 54.1-3207 Board of Optometry. <a class="header-anchor" href="#§-54-1-3207-board-of-optometry" aria-label="Permalink to &quot;§ 54.1-3207 Board of Optometry.&quot;">​</a></h1><p>The Board shall be composed of six members as follows: five licensed optometrists and one citizen member. Licensed optometrists appointed to the Board shall be individuals who, at the time of appointment, (i) have been engaged in the practice of optometry for at least five years, (ii) have met all requirements for practice as an optometrist set forth in this chapter and are qualified to engage in the full scope of the practice of optometry, and (iii) are actively engaged in the delivery of clinical care to patients. The terms of office of the members shall be four years.</p><p>Code 1950, §§ 54-371, 54-375; 1979, c. 39; 1986, c. 464; 1988, cc. 42, 765; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0152" target="_blank" rel="noreferrer">152</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0158" target="_blank" rel="noreferrer">158</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0340" target="_blank" rel="noreferrer">340</a>.</p>',3),n=[i];function s(c,l,f,p,d,h){return t(),a("div",null,n)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
