import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-3305 Board; membership; terms; meetings; quorum; officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3305.md","filePath":"vacode/54.1-3305.md"}'),s={name:"vacode/54.1-3305.md"},t=o('<h1 id="§-54-1-3305-board-membership-terms-meetings-quorum-officers" tabindex="-1">§ 54.1-3305 Board; membership; terms; meetings; quorum; officers. <a class="header-anchor" href="#§-54-1-3305-board-membership-terms-meetings-quorum-officers" aria-label="Permalink to &quot;§ 54.1-3305 Board; membership; terms; meetings; quorum; officers.&quot;">​</a></h1><p>The Board of Pharmacy shall consist of ten members, as follows: eight licensed pharmacists who are graduates of an approved school or college of pharmacy and two citizen members. The terms of office of the members shall be four years.</p><p>The Board shall meet at least annually at such times and places, and upon such notice as the Board may determine and as its business may require. A majority of the members of the Board shall constitute a quorum for the transaction of business.</p><p>The Board shall annually elect from its members a chairman.</p><p>There shall be an executive director for the Board of Pharmacy who shall be licensed or eligible for licensure in the Commonwealth as a pharmacist.</p><p>Code 1950, §§ 54-404, 54-405, 54-410, 54-412; 1958, c. 551; 1970, c. 650, §§ 54-524.5, 54-524.11; 1976, c. 614, § 54-524.13; 1986, c. 464, § 54-524.6; 1988, cc. 42, 765; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0070" target="_blank" rel="noreferrer">70</a>.</p>',6),i=[t];function c(m,n,l,h,f,d){return a(),r("div",null,i)}const _=e(s,[["render",c]]);export{u as __pageData,_ as default};
