import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-3408.02 Transmission of prescriptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3408.02.md","filePath":"vacode/54.1-3408.02.md"}'),a={name:"vacode/54.1-3408.02.md"},o=t('<h1 id="§-54-1-3408-02-transmission-of-prescriptions" tabindex="-1">§ 54.1-3408.02 Transmission of prescriptions. <a class="header-anchor" href="#§-54-1-3408-02-transmission-of-prescriptions" aria-label="Permalink to &quot;§ 54.1-3408.02 Transmission of prescriptions.&quot;">​</a></h1><p>A. Consistent with federal law and in accordance with regulations promulgated by the Board, prescriptions may be transmitted to a pharmacy as an electronic prescription or by facsimile machine and shall be treated as valid original prescriptions.</p><p>B. Any prescription for a controlled substance that contains an opioid shall be issued as an electronic prescription.</p><p>C. The requirements of subsection B shall not apply if:</p><ol><li><p>The prescriber dispenses the controlled substance that contains an opioid directly to the patient or the patient&#39;s agent;</p></li><li><p>The prescription is for an individual who is residing in a hospital, assisted living facility, nursing home, or residential health care facility or is receiving services from a hospice provider or outpatient dialysis facility;</p></li><li><p>The prescriber experiences temporary technological or electrical failure or other temporary extenuating circumstance that prevents the prescription from being transmitted electronically, provided that the prescriber documents the reason for this exception in the patient&#39;s medical record;</p></li><li><p>The prescriber issues a prescription to be dispensed by a pharmacy located on federal property, provided that the prescriber documents the reason for this exception in the patient&#39;s medical record;</p></li><li><p>The prescription is issued by a licensed veterinarian for the treatment of an animal;</p></li><li><p>The FDA requires the prescription to contain elements that are not able to be included in an electronic prescription;</p></li><li><p>The prescription is for an opioid under a research protocol;</p></li><li><p>The prescription is issued in accordance with an executive order of the Governor of a declared emergency;</p></li><li><p>The prescription cannot be issued electronically in a timely manner and the patient&#39;s condition is at risk, provided that the prescriber documents the reason for this exception in the patient&#39;s medical record; or</p></li><li><p>The prescriber has been issued a waiver pursuant to subsection D.</p></li></ol><p>D. The licensing health regulatory board of a prescriber may grant such prescriber, in accordance with regulations adopted by such board, a waiver of the requirements of subsection B, for a period not to exceed one year, due to demonstrated economic hardship, technological limitations that are not reasonably within the control of the prescriber, or other exceptional circumstances demonstrated by the prescriber.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0878" target="_blank" rel="noreferrer">878</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0429" target="_blank" rel="noreferrer">429</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>.</p>',7),n=[o];function s(c,p,l,h,d,f){return i(),r("div",null,n)}const b=e(a,[["render",s]]);export{u as __pageData,b as default};
