import{_ as e,o,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-3411.2:1 Guidelines for disposal of unused drugs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3411.2:1.md","filePath":"vacode/54.1-3411.2:1.md"}'),s={name:"vacode/54.1-3411.2:1.md"},r=i('<h1 id="§-54-1-3411-2-1-guidelines-for-disposal-of-unused-drugs" tabindex="-1">§ 54.1-3411.2:1 Guidelines for disposal of unused drugs. <a class="header-anchor" href="#§-54-1-3411-2-1-guidelines-for-disposal-of-unused-drugs" aria-label="Permalink to &quot;§ 54.1-3411.2:1 Guidelines for disposal of unused drugs.&quot;">​</a></h1><p>A. The Board of Pharmacy shall develop guidelines for the provision of counseling and information regarding proper disposal of unused dispensed drugs, including information about pharmacy drug disposal programs in which the pharmacy participates pursuant to § <a href="/vacode/54.1-3411.2/">54.1-3411.2</a>, by pharmacists to patients for whom a prescription is dispensed.</p><p>B. The Board of Pharmacy shall determine methods to enhance public awareness of proper drug disposal methods, which may include requirements for pharmacies or hospitals or clinics with an on-site pharmacy to provide such information to customers and the public through the provision of informative pamphlets, the posting of signs in public areas of the pharmacy, and the posting of information on public-facing websites.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0114" target="_blank" rel="noreferrer">114</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>.</p>',4),t=[r];function n(d,p,c,l,h,u){return o(),a("div",null,t)}const g=e(s,[["render",n]]);export{_ as __pageData,g as default};
