import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-3416 No prescription for preparations listed pursuant to Schedule V.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3416.md","filePath":"vacode/54.1-3416.md"}'),t={name:"vacode/54.1-3416.md"},i=a('<h1 id="§-54-1-3416-no-prescription-for-preparations-listed-pursuant-to-schedule-v" tabindex="-1">§ 54.1-3416 No prescription for preparations listed pursuant to Schedule V. <a class="header-anchor" href="#§-54-1-3416-no-prescription-for-preparations-listed-pursuant-to-schedule-v" aria-label="Permalink to &quot;§ 54.1-3416 No prescription for preparations listed pursuant to Schedule V.&quot;">​</a></h1><p>A preparation listed pursuant to Schedule V may be dispensed without a prescription if:</p><ol><li><p>The preparation is dispensed only by a pharmacist directly to the person requesting the preparation;</p></li><li><p>The preparation is dispensed only to a person who is at least eighteen years of age;</p></li><li><p>The pharmacist requires the person requesting the preparation to furnish suitable identification including proof of age when appropriate;</p></li><li><p>The pharmacist does not dispense to any one person, or for the use of any one person or animal, any narcotic drug preparation or preparations, when he knows, or can by reasonable diligence ascertain, that such dispensing will provide the person to whom or for whose use, or the owner of the animal for the use of which, such preparation is dispensed, within 48 consecutive hours, with more than 200 milligrams of opium, or more than 270 milligrams of codeine, or more than 130 milligrams of dihydrocodeinone, or more than 65 milligrams of ethylmorphine, or more than 32 5/10 milligrams of diphenoxylate. In dispensing such a narcotic drug preparation, the pharmacist shall exercise professional discretion to ensure that the preparation is being dispensed for medical purposes only.</p></li></ol><p>Any pharmacist shall, at the time of dispensing, make and keep a record showing the date of dispensing, the name and quantity of the preparation, the name and address of the person to whom the preparation is dispensed, and enter his initials thereon. Such records shall be maintained as set forth in § <a href="http://law.lis.virginia.gov/vacode/54.1-3404/" target="_blank" rel="noreferrer">54.1-3404</a> and the regulations of the Board.</p><p>1970, c. 650, § 54-524.75; 1972, c. 798; 1988, c. 765.</p>',5),n=[i];function s(p,h,d,l,c,u){return o(),r("div",null,n)}const _=e(t,[["render",s]]);export{f as __pageData,_ as default};
