import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3422 Controlled substances registration certificate required in addition to other requirements; exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3422.md","filePath":"vacode/54.1-3422.md"}'),s={name:"vacode/54.1-3422.md"},o=i('<h1 id="§-54-1-3422-controlled-substances-registration-certificate-required-in-addition-to-other-requirements-exemptions" tabindex="-1">§ 54.1-3422 Controlled substances registration certificate required in addition to other requirements; exemptions. <a class="header-anchor" href="#§-54-1-3422-controlled-substances-registration-certificate-required-in-addition-to-other-requirements-exemptions" aria-label="Permalink to &quot;§ 54.1-3422 Controlled substances registration certificate required in addition to other requirements; exemptions.&quot;">​</a></h1><p>A. Every person who manufactures, distributes or dispenses any substance that is controlled in Schedules I through V or who proposes to engage in the manufacture, distribution or dispensing of any such controlled substance except permitted pharmacies, those persons who are licensed pharmacists, those persons who are licensed physician assistants, and those persons who are licensed practitioners of medicine, osteopathy, podiatry, dentistry, optometry, nursing, or veterinary medicine shall obtain annually a controlled substances registration certificate issued by the Board. This registration shall be in addition to other licensing or permitting requirements enumerated in this chapter or otherwise required by law.</p><p>B. Registration under this section and under all other applicable registration requirements shall entitle the registrant to possess, manufacture, distribute, dispense, perform laboratory analysis, or conduct research with those substances to the extent authorized by this registration and in conformity with the other provisions of this chapter.</p><p>C. The following persons need not register and may possess controlled substances listed on Schedules I through VI:</p><ol><li><p>An agent or employee of any holder of a controlled substance registration certificate or of any practitioner listed in subsection A of this section as exempt from the requirement for registration, if such agent or employee is acting in the usual course of his business or employment;</p></li><li><p>A common or contract carrier or warehouseman, or his employee, whose possession is in the usual course of business or employment; or</p></li><li><p>An ultimate user or a person in possession of any controlled substance pursuant to a lawful order of a prescriber or in lawful possession of a Schedule V substance.</p></li></ol><p>D. A separate registration is required at each principal place of business or professional practice where the applicant manufactures, distributes, or dispenses controlled substances.</p><p>1972, c. 798, § 54-524.47:2; 1988, c. 765; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0408" target="_blank" rel="noreferrer">408</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0468" target="_blank" rel="noreferrer">468</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0496" target="_blank" rel="noreferrer">496</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0243" target="_blank" rel="noreferrer">243</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0465" target="_blank" rel="noreferrer">465</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>.</p>',7),n=[o];function a(l,c,p,h,u,d){return t(),r("div",null,n)}const m=e(s,[["render",a]]);export{g as __pageData,m as default};
