import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3435.2 Permit to act as medical equipment supplier; storage; limitation; regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3435.2.md","filePath":"vacode/54.1-3435.2.md"}'),r={name:"vacode/54.1-3435.2.md"},o=a('<h1 id="§-54-1-3435-2-permit-to-act-as-medical-equipment-supplier-storage-limitation-regulations" tabindex="-1">§ 54.1-3435.2 Permit to act as medical equipment supplier; storage; limitation; regulations. <a class="header-anchor" href="#§-54-1-3435-2-permit-to-act-as-medical-equipment-supplier-storage-limitation-regulations" aria-label="Permalink to &quot;§ 54.1-3435.2 Permit to act as medical equipment supplier; storage; limitation; regulations.&quot;">​</a></h1><p>A. Unless otherwise authorized by this chapter or Chapter 33 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3300/" target="_blank" rel="noreferrer">54.1-3300</a> et seq.) of this title, it shall be unlawful for any person to act as a medical equipment supplier, as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3401/" target="_blank" rel="noreferrer">54.1-3401</a>, in this Commonwealth without a valid unrevoked permit issued by the Board. The applicant for a permit to act as a medical equipment supplier in this Commonwealth shall apply to the Board for a permit, using such form as the Board may furnish; renew such permit, if granted, annually on a date determined by the Board in regulation; and remit a fee as determined by the Board.</p><p>B. Prescription drugs received, stored, and distributed by authority of this section shall be limited to those Schedule VI controlled substances with no medicinal properties which are used for the operation and cleaning of medical equipment, solutions for peritoneal dialysis, and sterile water and saline for irrigation.</p><p>C. Distribution of any Schedule VI drug or device or of any hypodermic needle or syringe, or medicinal oxygen by authority of this section is limited to delivery to the ultimate user upon lawful order by a prescriber authorized to prescribe such drugs and devices.</p><p>D. The Board may promulgate such regulations relating to the storage, handling, and distribution of prescription drugs, devices and controlled paraphernalia by medical equipment suppliers as it deems necessary to implement this section, to prevent diversion of prescription drugs and devices and controlled paraphernalia, and to protect the public.</p><p>1992, c. 737; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0408" target="_blank" rel="noreferrer">408</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0677" target="_blank" rel="noreferrer">677</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0320" target="_blank" rel="noreferrer">320</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0504" target="_blank" rel="noreferrer">504</a>.</p>',6),n=[o];function s(l,d,p,c,u,h){return t(),i("div",null,n)}const f=e(r,[["render",s]]);export{g as __pageData,f as default};
