import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3437 Permit to manufacture drugs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3437.md","filePath":"vacode/54.1-3437.md"}'),o={name:"vacode/54.1-3437.md"},n=t('<h1 id="§-54-1-3437-permit-to-manufacture-drugs" tabindex="-1">§ 54.1-3437 Permit to manufacture drugs. <a class="header-anchor" href="#§-54-1-3437-permit-to-manufacture-drugs" aria-label="Permalink to &quot;§ 54.1-3437 Permit to manufacture drugs.&quot;">​</a></h1><p>It shall be lawful to manufacture, make, produce, pack, package, repackage, relabel or prepare any drug not controlled by Schedule I after first obtaining the appropriate permit from the Board. Such permits shall be subject to the Board&#39;s regulations on sanitation, equipment, and safeguards against diversion, and shall allow the distribution of the drug manufactured, made, produced, packed, packaged, repackaged, relabeled, or prepared to anyone other than the end user without the need to obtain a wholesale distributor permit. This provision shall not apply to manufacturers or packers of medicated feeds who manufacture or package no other drugs.</p><p>Code 1950, § 54-448; 1958, c. 551; 1970, c. 650, § 54-524.36; 1988, c. 765; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0407" target="_blank" rel="noreferrer">407</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>.</p>',3),i=[n];function c(s,d,l,u,p,h){return a(),r("div",null,i)}const m=e(o,[["render",c]]);export{g as __pageData,m as default};
