import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 54.1-3442.3 Expanded access to investigational drugs, biological products, or devices; cost; insurance coverage.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3442.3.md","filePath":"vacode/54.1-3442.3.md"}'),r={name:"vacode/54.1-3442.3.md"},t=o('<h1 id="§-54-1-3442-3-expanded-access-to-investigational-drugs-biological-products-or-devices-cost-insurance-coverage" tabindex="-1">§ 54.1-3442.3 Expanded access to investigational drugs, biological products, or devices; cost; insurance coverage. <a class="header-anchor" href="#§-54-1-3442-3-expanded-access-to-investigational-drugs-biological-products-or-devices-cost-insurance-coverage" aria-label="Permalink to &quot;§ 54.1-3442.3 Expanded access to investigational drugs, biological products, or devices; cost; insurance coverage.&quot;">​</a></h1><p>A. A manufacturer of an investigational drug, biological product, or device may make such investigational drug, biological product, or device available to a person who meets the criteria set forth in subsection A of § <a href="http://law.lis.virginia.gov/vacode/54.1-3442.2/" target="_blank" rel="noreferrer">54.1-3442.2</a>; however, nothing in this article shall require a manufacturer of an investigational drug, biological product, or device to make such investigational drug, biological product, or device available to such person.</p><p>B. A manufacturer that makes an investigational drug, biological product, or device available to a person who meets the criteria set forth in subsection A of § <a href="http://law.lis.virginia.gov/vacode/54.1-3442.2/" target="_blank" rel="noreferrer">54.1-3442.2</a> may provide the investigational drug, biological product, or device to the person free of charge or may require the person to pay the costs of, or the costs associated with, the manufacture of the investigational drug, biological product, or device.</p><p>C. An insurer proposing to issue individual or group accident and sickness insurance policies providing hospital, medical and surgical, or major medical coverage on an expense-incurred basis, a corporation providing individual or group accident and sickness subscription contracts, or a health maintenance organization providing a health care plan for health care services may provide coverage for costs related to treatment of a person&#39;s terminal condition with an investigational drug, biological product, or device; however, nothing in this article shall require an insurer proposing to issue individual or group accident and sickness insurance policies providing hospital, medical and surgical, or major medical coverage on an expense-incurred basis, a corporation providing individual or group accident and sickness subscription contracts, or a health maintenance organization providing a health care plan for health care services to provide coverage for costs related to treatment of a person&#39;s terminal condition with an investigational drug, biological product, or device.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0655" target="_blank" rel="noreferrer">655</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0656" target="_blank" rel="noreferrer">656</a>.</p>',5),n=[t];function c(s,d,l,g,p,u){return a(),i("div",null,n)}const f=e(r,[["render",c]]);export{h as __pageData,f as default};
