import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-3442.4 Limitation of liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3442.4.md","filePath":"vacode/54.1-3442.4.md"}'),t={name:"vacode/54.1-3442.4.md"},o=r('<h1 id="§-54-1-3442-4-limitation-of-liability" tabindex="-1">§ 54.1-3442.4 Limitation of liability. <a class="header-anchor" href="#§-54-1-3442-4-limitation-of-liability" aria-label="Permalink to &quot;§ 54.1-3442.4 Limitation of liability.&quot;">​</a></h1><p>A. Notwithstanding any other provision of law to the contrary, a health care provider as defined in § <a href="http://law.lis.virginia.gov/vacode/8.01-581.1/" target="_blank" rel="noreferrer">8.01-581.1</a> who recommends an investigational drug, biological product, or device to a person who meets the criteria set forth in subsection A of § <a href="http://law.lis.virginia.gov/vacode/54.1-3442.2/" target="_blank" rel="noreferrer">54.1-3442.2</a> shall be immune from civil liability for any adverse action, condition, or other outcome resulting from the person&#39;s use of the investigational drug, biological product, or device.</p><p>B. Notwithstanding any other provision of law to the contrary, a manufacturer, distributor, administrator, health care provider as defined in § <a href="http://law.lis.virginia.gov/vacode/8.01-581.1/" target="_blank" rel="noreferrer">8.01-581.1</a>, sponsor, or physician who manufactures, supplies, distributes, administers, prescribes, or recommends an investigational drug, biological product, or device to a person who meets the criteria set forth in § <a href="http://law.lis.virginia.gov/vacode/54.1-3442.2/" target="_blank" rel="noreferrer">54.1-3442.2</a> shall be immune from suit and liability caused by, arising out of, or relating to the design, development, clinical testing and investigation, manufacture, labeling, distribution, sale, purchase, donation, dispensing, prescription, recommendation, administration, efficacy, or use of such investigational drug, biological product, or device made available to such person.</p><p>C. No claim or cause of action against a manufacturer, distributor, administrator, health care provider as defined in § <a href="http://law.lis.virginia.gov/vacode/8.01-581.1/" target="_blank" rel="noreferrer">8.01-581.1</a>, sponsor, or physician who manufactures, supplies, distributes, administers, prescribes, or recommends an investigational drug, biological product, or device to a person who meets the criteria set forth in § <a href="http://law.lis.virginia.gov/vacode/54.1-3442.2/" target="_blank" rel="noreferrer">54.1-3442.2</a> shall exist in any state court for claims of property, personal injury, or death caused by, arising out of, or relating to the design, development, clinical testing and investigation, manufacture, labeling, distribution, sale, purchase, donation, dispensing, prescription, recommendation, administration, efficacy, or use of such investigational drug, biological product, or device made available to such person.</p><p>D. No health care provider as defined in § <a href="http://law.lis.virginia.gov/vacode/8.01-581.1/" target="_blank" rel="noreferrer">8.01-581.1</a> who recommends, prescribes, administers, distributes, or supplies an investigational drug, biological product, or device to a person who meets the criteria set forth in § <a href="http://law.lis.virginia.gov/vacode/54.1-3442.2/" target="_blank" rel="noreferrer">54.1-3442.2</a> shall be deemed to have engaged in unprofessional conduct, or shall be adversely affected in any decision relating to licensure, on such grounds.</p><p>E. Nothing in this article shall require a person to violate or act in contravention of any federal or state law as such law relates to the prescribing, dispensing, administration, or use of an investigational drug, biological product, or device.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0655" target="_blank" rel="noreferrer">655</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0656" target="_blank" rel="noreferrer">656</a>.</p>',7),n=[o];function s(l,c,d,h,p,g){return i(),a("div",null,n)}const v=e(t,[["render",s]]);export{f as __pageData,v as default};
