import{_ as e,o as i,c as s,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3467 Distribution of hypodermic needles or syringes, gelatin capsules, quinine or any of its salts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3467.md","filePath":"vacode/54.1-3467.md"}'),o={name:"vacode/54.1-3467.md"},r=n('<h1 id="§-54-1-3467-distribution-of-hypodermic-needles-or-syringes-gelatin-capsules-quinine-or-any-of-its-salts" tabindex="-1">§ 54.1-3467 Distribution of hypodermic needles or syringes, gelatin capsules, quinine or any of its salts. <a class="header-anchor" href="#§-54-1-3467-distribution-of-hypodermic-needles-or-syringes-gelatin-capsules-quinine-or-any-of-its-salts" aria-label="Permalink to &quot;§ 54.1-3467 Distribution of hypodermic needles or syringes, gelatin capsules, quinine or any of its salts.&quot;">​</a></h1><p>A. Distribution by any method, of any hypodermic needles or syringes, gelatin capsules, quinine or any of its salts, in excess of one-fourth ounce shall be restricted to licensed pharmacists or to others who have received a license or a permit from the Board.</p><p>B. Nothing in this section shall prohibit the dispensing or distributing of hypodermic needles and syringes by persons authorized by the State Health Commissioner pursuant to a comprehensive harm reduction program established pursuant to § <a href="/vacode/32.1-45.4/">32.1-45.4</a> who are acting in accordance with the standards and protocols of such program for the duration of the declared public health emergency.</p><p>C. Nothing in this section shall prohibit the dispensing or distributing of hypodermic needles and syringes by persons authorized to dispense naloxone in accordance with the provisions of subsection Y of § <a href="/vacode/54.1-3408/">54.1-3408</a> and who, in conjunction with such dispensing of naloxone, dispenses or distributes hypodermic needles and syringes. Nothing in this section shall prohibit the dispensing of hypodermic needles and syringes for the administration of prescribed drugs by prescribers licensed to dispense Schedule VI controlled substances at a nonprofit facility pursuant to § <a href="/vacode/54.1-3304.1/">54.1-3304.1</a>.</p><p>1971, Ex. Sess., cc. 210, 245; 1988, c. 765; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0183" target="_blank" rel="noreferrer">183</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0097" target="_blank" rel="noreferrer">97</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0609" target="_blank" rel="noreferrer">609</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0610" target="_blank" rel="noreferrer">610</a>.</p>',5),t=[r];function a(c,l,d,h,p,f){return i(),s("div",null,t)}const y=e(o,[["render",a]]);export{g as __pageData,y as default};
