import{_ as a,o as e,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-3470 Obtaining controlled paraphernalia by fraud, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3470.md","filePath":"vacode/54.1-3470.md"}'),o={name:"vacode/54.1-3470.md"},n=t('<h1 id="§-54-1-3470-obtaining-controlled-paraphernalia-by-fraud-etc" tabindex="-1">§ 54.1-3470 Obtaining controlled paraphernalia by fraud, etc. <a class="header-anchor" href="#§-54-1-3470-obtaining-controlled-paraphernalia-by-fraud-etc" aria-label="Permalink to &quot;§ 54.1-3470 Obtaining controlled paraphernalia by fraud, etc.&quot;">​</a></h1><p>A. No person shall obtain or attempt to obtain any item, device or substance referred to in § <a href="http://law.lis.virginia.gov/vacode/54.1-3467/" target="_blank" rel="noreferrer">54.1-3467</a> by fraud, deceit, misrepresentation, or subterfuge or by giving a false name or a false address.</p><p>B. No person shall furnish false or fraudulent information in or omit any information from, or willfully make a false statement in obtaining or attempting to obtain any of the instruments or substances referred to in § <a href="http://law.lis.virginia.gov/vacode/54.1-3467/" target="_blank" rel="noreferrer">54.1-3467</a>.</p><p>C. No person shall, for the purpose of obtaining any such instrument or substance, falsely claim to be a manufacturer, wholesaler, pharmacist, practitioner of the healing arts, funeral director, embalmer or veterinarian.</p><p>Persons violating the provisions of this section shall be guilty of a Class 1 misdemeanor.</p><p>1971, Ex. Sess., cc. 210, 245; 1988, c. 765.</p>',6),i=[n];function s(l,c,p,d,f,h){return e(),r("div",null,i)}const u=a(o,[["render",s]]);export{m as __pageData,u as default};
