import{_ as e,o as t,c as s,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-3481 Unlawful designation as physical therapist or physical therapist assistant; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3481.md","filePath":"vacode/54.1-3481.md"}'),i={name:"vacode/54.1-3481.md"},r=a('<h1 id="§-54-1-3481-unlawful-designation-as-physical-therapist-or-physical-therapist-assistant-penalty" tabindex="-1">§ 54.1-3481 Unlawful designation as physical therapist or physical therapist assistant; penalty. <a class="header-anchor" href="#§-54-1-3481-unlawful-designation-as-physical-therapist-or-physical-therapist-assistant-penalty" aria-label="Permalink to &quot;§ 54.1-3481 Unlawful designation as physical therapist or physical therapist assistant; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person who is not licensed under this chapter, or whose license has been suspended or revoked or who licensure has lapsed and has not been renewed, to use in conjunction with his name the letters or words &quot;R.P.T.,&quot; &quot;Registered Physical Therapist,&quot; &quot;L.P.T.,&quot; &quot;Licensed Physical Therapist,&quot; &quot;P.T.,&quot; &quot;Physical Therapist,&quot; &quot;Physio-therapist,&quot; &quot;P.T.T.,&quot; &quot;Physical Therapy Technician,&quot; &quot;P.T.A.,&quot; &quot;Physical Therapist Assistant,&quot; &quot;Licensed Physical Therapist Assistant,&quot; or to otherwise by letters, words, representations or insignias assert or imply that he is a licensed physical therapist. The title to designate a licensed physical therapist shall be &quot;P.T.&quot; The title to designate a physical therapist assistant shall show such fact plainly on its face.</p><p>B. No person shall advertise services using the words &quot;physical therapy&quot; or &quot;physiotherapy&quot; unless those services are provided by a physical therapist or physical therapist assistant licensed pursuant to this chapter.</p><p>C. A complaint or report of a possible violation of this section by any person who is licensed, certified, registered, or permitted, or who holds a multistate licensure privilege issued by any of the health regulatory boards within the Department of Health Professions shall be referred to the applicable board within the Department for disciplinary action.</p><p>D. Nothing in this section shall be construed to restrict or limit the legally authorized scope of practice of any profession licensed, certified, registered, permitted, or recognized under a multistate licensure privilege issued by any of the health regulatory boards within the Department of Health Professions prior to January 1, 2010.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0070" target="_blank" rel="noreferrer">70</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0368" target="_blank" rel="noreferrer">368</a>.</p>',6),o=[r];function n(l,h,p,c,u,d){return t(),s("div",null,o)}const g=e(i,[["render",n]]);export{f as __pageData,g as default};
