import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3482.1 Certain certification required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3482.1.md","filePath":"vacode/54.1-3482.1.md"}'),t={name:"vacode/54.1-3482.1.md"},n=a('<h1 id="§-54-1-3482-1-certain-certification-required" tabindex="-1">§ 54.1-3482.1 Certain certification required. <a class="header-anchor" href="#§-54-1-3482-1-certain-certification-required" aria-label="Permalink to &quot;§ 54.1-3482.1 Certain certification required.&quot;">​</a></h1><p>A. The Board shall promulgate regulations establishing criteria for certification of physical therapists to provide certain physical therapy services pursuant to subsection B of § <a href="/vacode/54.1-3482/">54.1-3482</a> without referral from a licensed doctor of medicine, osteopathy, chiropractic, podiatry, or dental surgery, a licensed nurse practitioner practicing in accordance with the provisions of § <a href="/vacode/54.1-2957/">54.1-2957</a>, or a licensed physician assistant acting under the supervision of a licensed physician. The regulations shall include but not be limited to provisions for (i) the promotion of patient safety; (ii) an application process for a one-time certification to perform such procedures; and (iii) minimum education, training, and experience requirements for certification to perform such procedures.</p><p>B. The minimum education, training, and experience requirements for certification shall include evidence that the applicant has successfully completed (i) a transitional program in physical therapy as recognized by the Board or (ii) at least three years of active practice with evidence of continuing education relating to carrying out direct access duties under § <a href="/vacode/54.1-3482/">54.1-3482</a>.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0018" target="_blank" rel="noreferrer">18</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0724" target="_blank" rel="noreferrer">724</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0746" target="_blank" rel="noreferrer">746</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0776" target="_blank" rel="noreferrer">776</a>.</p>',4),o=[n];function c(s,l,p,d,f,h){return i(),r("div",null,o)}const _=e(t,[["render",c]]);export{g as __pageData,_ as default};
