import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3483 Unprofessional conduct.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3483.md","filePath":"vacode/54.1-3483.md"}'),o={name:"vacode/54.1-3483.md"},n=i('<h1 id="§-54-1-3483-unprofessional-conduct" tabindex="-1">§ 54.1-3483 Unprofessional conduct. <a class="header-anchor" href="#§-54-1-3483-unprofessional-conduct" aria-label="Permalink to &quot;§ 54.1-3483 Unprofessional conduct.&quot;">​</a></h1><p>Any physical therapist or physical therapist assistant licensed by the Board or practicing pursuant to a compact privilege, as defined in § <a href="/vacode/54.1-3486/">54.1-3486</a>, approved by the Board shall be considered guilty of unprofessional conduct if he:</p><ol><li><p>Engages in the practice of physical therapy under a false or assumed name or impersonates another practitioner of a like, similar or different name;</p></li><li><p>Knowingly and willfully commits any act which is a felony under the laws of this Commonwealth or the United States, or any act which is a misdemeanor under such laws and involves moral turpitude;</p></li><li><p>Aids or abets, has professional contact with, or lends his name to any person known to him to be practicing physical therapy illegally;</p></li><li><p>Conducts his practice in such a manner as to be a danger to the health and welfare of his patients or to the public;</p></li><li><p>Is unable to practice with reasonable skill or safety because of illness or substance abuse;</p></li><li><p>Publishes in any manner an advertisement that violates Board regulations governing advertising;</p></li><li><p>Performs any act likely to deceive, defraud or harm the public;</p></li><li><p>Violates any provision of statute or regulation, state or federal, relating to controlled substances;</p></li><li><p>Violates or cooperates with others in violating any of the provisions of this chapter or regulations of the Board; or</p></li><li><p>Engages in sexual contact with a patient concurrent with and by virtue of the practitioner/patient relationship or otherwise engages at any time during the course of the practitioner/patient relationship in conduct of a sexual nature that a reasonable patient would consider lewd and offensive.</p></li></ol><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>.</p>',4),r=[n];function s(l,c,p,h,d,u){return a(),t("div",null,r)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
