import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3506.1 Client notification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3506.1.md","filePath":"vacode/54.1-3506.1.md"}'),o={name:"vacode/54.1-3506.1.md"},r=n('<h1 id="§-54-1-3506-1-client-notification" tabindex="-1">§ 54.1-3506.1 Client notification. <a class="header-anchor" href="#§-54-1-3506-1-client-notification" aria-label="Permalink to &quot;§ 54.1-3506.1 Client notification.&quot;">​</a></h1><p>Any person licensed, certified, or registered by the Board and operating in a nonhospital setting shall post a copy of his license, certification, or registration in a conspicuous place. The posting shall also provide clients with (i) the number of the toll-free complaint line at the Department of Health Professions, (ii) the website address of the Department for the purposes of accessing the licensee&#39;s, certificate holder&#39;s, or registrant&#39;s record, and (iii) notice of the client&#39;s right to report to the Department if he believes the licensee, certificate holder, or registrant may have engaged in unethical, fraudulent, or unprofessional conduct. If the licensee, certificate holder, or registrant does not operate in a central location at which clients visit, he or his employer shall provide such information on a disclosure form signed by the client and maintained in the client&#39;s record.</p><p>2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0530" target="_blank" rel="noreferrer">530</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0418" target="_blank" rel="noreferrer">418</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0426" target="_blank" rel="noreferrer">426</a>.</p>',3),a=[r];function s(c,l,h,f,d,p){return t(),i("div",null,a)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
