import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-3508 Licensure of certain persons possessing substantially equivalent qualifications, education or experience.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3508.md","filePath":"vacode/54.1-3508.md"}'),s={name:"vacode/54.1-3508.md"},n=i('<h1 id="§-54-1-3508-licensure-of-certain-persons-possessing-substantially-equivalent-qualifications-education-or-experience" tabindex="-1">§ 54.1-3508 Licensure of certain persons possessing substantially equivalent qualifications, education or experience. <a class="header-anchor" href="#§-54-1-3508-licensure-of-certain-persons-possessing-substantially-equivalent-qualifications-education-or-experience" aria-label="Permalink to &quot;§ 54.1-3508 Licensure of certain persons possessing substantially equivalent qualifications, education or experience.&quot;">​</a></h1><p>Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/54.1-3507/" target="_blank" rel="noreferrer">54.1-3507</a>, (i) the Board may issue a license as a licensed substance abuse treatment practitioner to a person who, after the effective date of the regulations promulgated pursuant to subdivision 7 of § <a href="http://law.lis.virginia.gov/vacode/54.1-3505/" target="_blank" rel="noreferrer">54.1-3505</a>, has applied for such a license and who, in the judgment of the Board, possesses qualifications, education or experience substantially equivalent to the requirements of § <a href="http://law.lis.virginia.gov/vacode/54.1-3507/" target="_blank" rel="noreferrer">54.1-3507</a>; however, any such applicant shall have completed at least one year of supervised clinical experience in substance abuse treatment, and (ii) for a period of time to be determined by the Board but not less than one year after the effective date of the regulations, the Board shall issue such a license to any such person who, in the judgment of the Board, possesses qualifications, education or experience acceptable to the Board and has completed at least one year of supervised clinical experience in substance abuse treatment.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0901" target="_blank" rel="noreferrer">901</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>.</p>',3),r=[n];function o(c,l,u,p,d,f){return a(),t("div",null,r)}const v=e(s,[["render",o]]);export{_ as __pageData,v as default};
