import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-3605 Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3605.md","filePath":"vacode/54.1-3605.md"}'),t={name:"vacode/54.1-3605.md"},o=a('<h1 id="§-54-1-3605-powers-and-duties-of-the-board" tabindex="-1">§ 54.1-3605 Powers and duties of the Board. <a class="header-anchor" href="#§-54-1-3605-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 54.1-3605 Powers and duties of the Board.&quot;">​</a></h1><p>In addition to the powers granted in other provisions of this title, the Board shall have the following specific powers and duties:</p><ol><li><p>To cooperate with and maintain a close liaison with other professional boards and the community to ensure that regulatory systems stay abreast of community and professional needs.</p></li><li><p>To conduct inspections to ensure that licensees conduct their practices in a competent manner and in conformance with the relevant regulations.</p></li><li><p>To designate specialties within the profession.</p></li><li><p>To issue a temporary license for such periods as the Board may prescribe to practice psychology to persons who are engaged in a residency or pursuant to subdivision 7 of § <a href="http://law.lis.virginia.gov/vacode/54.1-3601/" target="_blank" rel="noreferrer">54.1-3601</a>.</p></li><li><p>To promulgate regulations for the voluntary certification of licensees as sex offender treatment providers.</p></li><li><p>To administer the mandatory certification of sex offender treatment providers for those professionals who are otherwise exempt from licensure under subdivision 4 of §§ <a href="http://law.lis.virginia.gov/vacode/54.1-3501/" target="_blank" rel="noreferrer">54.1-3501</a>, <a href="http://law.lis.virginia.gov/vacode/54.1-3601/" target="_blank" rel="noreferrer">54.1-3601</a> or § <a href="http://law.lis.virginia.gov/vacode/54.1-3701/" target="_blank" rel="noreferrer">54.1-3701</a> and to promulgate regulations governing such mandatory certification. The regulations shall include provisions for fees for application processing, certification qualifications, certification issuance and renewal and disciplinary action.</p></li><li><p>To promulgate regulations establishing the requirements for licensure of clinical psychologists that shall include appropriate emphasis in the diagnosis and treatment of persons with moderate and severe mental disorders.</p></li></ol><p>1976, c. 608, §§ 54-929, 54-931; 1983, c. 115; 1986, cc. 64, 100, 464; 1988, c. 765; 1993, c. 767; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0778" target="_blank" rel="noreferrer">778</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0980" target="_blank" rel="noreferrer">980</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0556" target="_blank" rel="noreferrer">556</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0630" target="_blank" rel="noreferrer">630</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0186" target="_blank" rel="noreferrer">186</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0198" target="_blank" rel="noreferrer">198</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0011" target="_blank" rel="noreferrer">11</a>.</p>',4),n=[o];function s(l,c,p,d,f,h){return r(),i("div",null,n)}const v=e(t,[["render",s]]);export{u as __pageData,v as default};
