import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-3705 Specific powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3705.md","filePath":"vacode/54.1-3705.md"}'),o={name:"vacode/54.1-3705.md"},r=t('<h1 id="§-54-1-3705-specific-powers-and-duties-of-the-board" tabindex="-1">§ 54.1-3705 Specific powers and duties of the Board. <a class="header-anchor" href="#§-54-1-3705-specific-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 54.1-3705 Specific powers and duties of the Board.&quot;">​</a></h1><p>In addition to the powers granted in § <a href="/vacode/54.1-2400/">54.1-2400</a>, the Board shall have the following specific powers and duties:</p><ol><li><p>To cooperate with and maintain a close liaison with other professional boards and the community to ensure that regulatory systems stay abreast of community and professional needs.</p></li><li><p>To conduct inspections to ensure that licensees conduct their practices in a competent manner and in conformance with the relevant regulations.</p></li><li><p>To designate specialties within the profession.</p></li><li><p>Expired.</p></li><li><p>To license baccalaureate social workers, master&#39;s social workers, and clinical social workers to practice consistent with the requirements of the chapter and regulations of the Board.</p></li><li><p>To register persons proposing to obtain supervised post-degree experience in the practice of social work required by the Board for licensure as a clinical social worker.</p></li><li><p>To pursue the establishment of reciprocal agreements with jurisdictions that are contiguous with the Commonwealth for the licensure of baccalaureate social workers, master&#39;s social workers, and clinical social workers. Reciprocal agreements shall require that a person hold a comparable, current, unrestricted license in the other jurisdiction and that no grounds exist for denial based on the Code of Virginia and regulations of the Board.</p></li></ol><p>1976, c. 608, §§ 54-929, 54-931; 1983, c. 115; 1986, cc. 64, 100, 464; 1988, c. 765; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0778" target="_blank" rel="noreferrer">778</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0451" target="_blank" rel="noreferrer">451</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0617" target="_blank" rel="noreferrer">617</a>.</p>',4),s=[r];function n(c,l,p,d,h,f){return a(),i("div",null,s)}const g=e(o,[["render",n]]);export{_ as __pageData,g as default};
