import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-3804 Specific powers of Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3804.md","filePath":"vacode/54.1-3804.md"}'),t={name:"vacode/54.1-3804.md"},o=i('<h1 id="§-54-1-3804-specific-powers-of-board" tabindex="-1">§ 54.1-3804 Specific powers of Board. <a class="header-anchor" href="#§-54-1-3804-specific-powers-of-board" aria-label="Permalink to &quot;§ 54.1-3804 Specific powers of Board.&quot;">​</a></h1><p>In addition to the powers granted in § <a href="http://law.lis.virginia.gov/vacode/54.1-2400/" target="_blank" rel="noreferrer">54.1-2400</a>, the Board shall have the following specific powers and duties:</p><ol><li><p>To establish essential requirements and standards for approval of veterinary programs.</p></li><li><p>To establish and monitor programs for the practical training of qualified students of veterinary medicine or veterinary technology in programs of veterinary medicine or veterinary technology at institutions of higher education.</p></li><li><p>To regulate, inspect, and register all establishments and premises where veterinary medicine is practiced.</p></li><li><p>To establish requirements for the licensure of persons engaged in the practice of veterinary medicine, pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-3800/" target="_blank" rel="noreferrer">54.1-3800</a>, as part of a veterinary medical education program accredited by the American Veterinary Medical Association Council on Education and located in the Commonwealth.</p></li></ol><p>1978, c. 539, § 54-784.03; 1988, c. 765; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0306" target="_blank" rel="noreferrer">306</a>.</p>',4),n=[o];function s(c,l,d,p,f,h){return a(),r("div",null,n)}const g=e(t,[["render",s]]);export{m as __pageData,g as default};
