import{_ as e,o as a,c as n,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-3807 Refusal to grant and to renew; revocation and suspension of licenses and registrations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3807.md","filePath":"vacode/54.1-3807.md"}'),i={name:"vacode/54.1-3807.md"},o=r('<h1 id="§-54-1-3807-refusal-to-grant-and-to-renew-revocation-and-suspension-of-licenses-and-registrations" tabindex="-1">§ 54.1-3807 Refusal to grant and to renew; revocation and suspension of licenses and registrations. <a class="header-anchor" href="#§-54-1-3807-refusal-to-grant-and-to-renew-revocation-and-suspension-of-licenses-and-registrations" aria-label="Permalink to &quot;§ 54.1-3807 Refusal to grant and to renew; revocation and suspension of licenses and registrations.&quot;">​</a></h1><p>The Board may refuse to grant or to renew, may suspend, or may revoke any license to practice veterinary medicine or to practice as a veterinary technician or registration to practice as an equine dental technician if such applicant or holder:</p><ol><li><p>Is convicted of any felony or of any misdemeanor involving moral turpitude;</p></li><li><p>Employs or permits any person who does not hold a license to practice veterinary medicine or to practice as a licensed veterinary technician or registration to practice as an equine dental technician to perform work which can lawfully be performed only by a person holding the appropriate license or registration;</p></li><li><p>Willfully violates any provision of this chapter or any regulation of the Board;</p></li><li><p>Has violated any federal or state law relating to controlled substances as defined in Chapter 34 (§ <a href="/vacode/54.1-3400/">54.1-3400</a> et seq.);</p></li><li><p>Is guilty of unprofessional conduct as defined by regulations of the Board;</p></li><li><p>Uses alcohol or drugs to the extent such use renders him unsafe to practice or suffers from any mental or physical condition rendering him unsafe to practice; or</p></li><li><p>Has had his license to practice veterinary medicine or as a veterinary technician or his registration to practice as an equine dental technician in any other state revoked or suspended for any reason other than nonrenewal or has surrendered such license or registration in lieu of disciplinary action.</p></li></ol><p>1978, c. 539, § 54-786.4; 1979, c. 243; 1988, c. 765; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0479" target="_blank" rel="noreferrer">479</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>.</p>',4),t=[o];function s(c,l,d,p,f,h){return a(),n("div",null,t)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
