import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-3812 Release of records.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3812.md","filePath":"vacode/54.1-3812.md"}'),t={name:"vacode/54.1-3812.md"},o=i('<h1 id="§-54-1-3812-release-of-records" tabindex="-1">§ 54.1-3812 Release of records. <a class="header-anchor" href="#§-54-1-3812-release-of-records" aria-label="Permalink to &quot;§ 54.1-3812 Release of records.&quot;">​</a></h1><p>A. A veterinarian licensed by the Board shall release or authorize the release of rabies immunization records and other relevant treatment data of an animal under his care to (i) a requesting physician, physician assistant, or nurse practitioner who is contemplating the administration of the rabies treatment protocol to any person under his care who has been the victim of a bite or other possible rabies exposure from such animal; (ii) a requesting animal control officer or law-enforcement officer who needs to identify the owner of such animal or verify the rabies vaccination history of such animal; or (iii) a requesting animal control officer or an official of the Department of Health who is investigating the incident.</p><p>B. Any veterinarian licensed by the Board who in good faith releases or authorizes the release of an animal&#39;s rabies immunization records and other relevant data pursuant to this section shall not be liable for civil damages resulting from the release of such information.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0855" target="_blank" rel="noreferrer">855</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0396" target="_blank" rel="noreferrer">396</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>.</p>',4),n=[o];function s(l,c,h,f,d,p){return a(),r("div",null,n)}const g=e(t,[["render",s]]);export{m as __pageData,g as default};
