import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-3926 Preliminary proof of education required of applicant.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-3926.md","filePath":"vacode/54.1-3926.md"}'),o={name:"vacode/54.1-3926.md"},i=r('<h1 id="§-54-1-3926-preliminary-proof-of-education-required-of-applicant" tabindex="-1">§ 54.1-3926 Preliminary proof of education required of applicant. <a class="header-anchor" href="#§-54-1-3926-preliminary-proof-of-education-required-of-applicant" aria-label="Permalink to &quot;§ 54.1-3926 Preliminary proof of education required of applicant.&quot;">​</a></h1><p>Before an applicant will be permitted to take any examination under this article, the applicant shall furnish to the Board satisfactory evidence that he has:</p><ol><li><p>Satisfactorily completed legal studies amounting to at least five semesters, or the equivalent of at least five semesters on a system other than a semester system, of full-time study at a law school approved by the American Bar Association or the Board;</p></li><li><p>Received a bachelor&#39;s degree from an accredited baccalaureate institution of higher education and studied law for three years, consisting of not less than 18 hours per week for at least 40 weeks per year in the office of an attorney practicing in the Commonwealth, whose full time is devoted to the practice of law;</p></li><li><p>Studied law for at least three years partly in a law school approved by the American Bar Association or the Board and partly, for not less than 18 hours per week for at least 40 weeks per year, in the office of an attorney practicing in the Commonwealth whose full time is devoted to the practice of law;</p></li><li><p>Received a bachelor&#39;s degree from an accredited baccalaureate institution of higher education and studied law for three years, consisting of not less than 18 hours per week for at least 40 weeks per year, with a retired circuit court judge who served the Commonwealth as a circuit court judge for a minimum of 10 years and who at the time of commencement of the three-year study period was retired for not more than five years; or</p></li><li><p>Completed all degree requirements from a law school not approved by the American Bar Association, including a foreign law school, obtained an LL.M. from a law school approved by the American Bar Association, and been admitted to practice law before the court of last resort in any state or territory of the United States or the District of Columbia.</p></li></ol><p>The attorney in whose office or the judge with whom the applicant intends to study shall be approved by the Board, which shall prescribe reasonable conditions as to the course of study.</p><p>Code 1950, § 54-62; 1952, c. 553; 1960, c. 379; 1980, c. 533; 1988, c. 765; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0336" target="_blank" rel="noreferrer">336</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0119" target="_blank" rel="noreferrer">119</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0296" target="_blank" rel="noreferrer">296</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',5),n=[i];function l(s,c,h,d,p,f){return a(),t("div",null,n)}const g=e(o,[["render",l]]);export{m as __pageData,g as default};
