import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-4005 Sale of goods pawned.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-4005.md","filePath":"vacode/54.1-4005.md"}'),o={name:"vacode/54.1-4005.md"},i=t('<h1 id="§-54-1-4005-sale-of-goods-pawned" tabindex="-1">§ 54.1-4005 Sale of goods pawned. <a class="header-anchor" href="#§-54-1-4005-sale-of-goods-pawned" aria-label="Permalink to &quot;§ 54.1-4005 Sale of goods pawned.&quot;">​</a></h1><p>No pawnbroker shall sell any pawn or pledge item until (i) it has been in his possession for the minimum term set forth in the memorandum, but not less than 30 days, plus a grace period of 15 days and (ii) a statement of ownership is obtained from the pawner. If a motor vehicle is pawned, the owner of the motor vehicle shall comply with the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-637/" target="_blank" rel="noreferrer">46.2-637</a>. In the event of default by the pawner, the pawnbroker shall comply with the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-633/" target="_blank" rel="noreferrer">46.2-633</a>. Otherwise, the pawnbroker shall comply with the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-636/" target="_blank" rel="noreferrer">46.2-636</a> et seq. All sales of items pursuant to this section may be made by the pawnbroker in the ordinary course of his business.</p><p>Code 1950, § 54-847; 1986, c. 316; 1988, c. 765; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0848" target="_blank" rel="noreferrer">848</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0327" target="_blank" rel="noreferrer">327</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>.</p>',3),n=[i];function s(l,h,p,d,c,f){return a(),r("div",null,n)}const m=e(o,[["render",s]]);expo