import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-4014 Penalties; violation of the Virginia Consumer Protection Act.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-4014.md","filePath":"vacode/54.1-4014.md"}'),i={name:"vacode/54.1-4014.md"},r=a('<h1 id="§-54-1-4014-penalties-violation-of-the-virginia-consumer-protection-act" tabindex="-1">§ 54.1-4014 Penalties; violation of the Virginia Consumer Protection Act. <a class="header-anchor" href="#§-54-1-4014-penalties-violation-of-the-virginia-consumer-protection-act" aria-label="Permalink to &quot;§ 54.1-4014 Penalties; violation of the Virginia Consumer Protection Act.&quot;">​</a></h1><p>A. Except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/54.1-4001/" target="_blank" rel="noreferrer">54.1-4001</a>, any licensed pawnbroker who violates any of the provisions of this chapter shall be guilty of a Class 4 misdemeanor. In addition, the court may revoke or suspend the pawnbroker&#39;s license for second and subsequent offenses.</p><p>B. Additionally, any violation of the provisions of the chapter shall constitute a prohibited practice in accordance with § <a href="http://law.lis.virginia.gov/vacode/59.1-200/" target="_blank" rel="noreferrer">59.1-200</a> and shall be subject to any and all of the enforcement provisions of the Virginia Consumer Protection Act (§ <a href="http://law.lis.virginia.gov/vacode/59.1-196/" target="_blank" rel="noreferrer">59.1-196</a> et seq.).</p><p>Code 1950, §§ 54-857, 54-858; 1988, c. 765; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0848" target="_blank" rel="noreferrer">848</a>.</p>',4),n=[r];function s(c,l,h,d,p,f){return t(),o("div",null,n)}const g=e(i,[["render",s]]);export{v as __pageData,g as default};
