import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-402.2 Cease and desist orders for unlicensed activity; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-402.2.md","filePath":"vacode/54.1-402.2.md"}'),a={name:"vacode/54.1-402.2.md"},n=i('<h1 id="§-54-1-402-2-cease-and-desist-orders-for-unlicensed-activity-civil-penalty" tabindex="-1">§ 54.1-402.2 Cease and desist orders for unlicensed activity; civil penalty. <a class="header-anchor" href="#§-54-1-402-2-cease-and-desist-orders-for-unlicensed-activity-civil-penalty" aria-label="Permalink to &quot;§ 54.1-402.2 Cease and desist orders for unlicensed activity; civil penalty.&quot;">​</a></h1><p>A. Notwithstanding § <a href="http://law.lis.virginia.gov/vacode/54.1-111/" target="_blank" rel="noreferrer">54.1-111</a>, the Board may issue an order requiring any person to cease and desist from (i) practicing or offering to practice as an architect, professional engineer, land surveyor, or landscape architect when such person is not licensed or registered by the Board in accordance with this chapter or (ii) holding himself out as a certified interior designer when such person is not certified or registered by the Board in accordance with this chapter. The order shall be effective upon its entry and shall become final unless such person files an appeal with the Board in accordance with the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) within 21 days of the date of entry of the order.</p><p>B. If the person fails to cease and desist the unlicensed, uncertified, or unregistered activity after entry of an order in accordance with subsection A, the Board may refer the matter for enforcement pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-306/" target="_blank" rel="noreferrer">54.1-306</a>.</p><p>C. Any person engaging in unlicensed, uncertified, or unregistered activity shall be subject to further proceedings before the Board and the Board may impose a civil penalty not to exceed $2,500. Any penalties collected under this section shall be paid to the Literary Fund after deduction of the administrative costs of the Board in furtherance of this section.</p><p>D. Nothing contained in this section shall apply to any person engaged in activity exempted from the provisions of this chapter.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0618" target="_blank" rel="noreferrer">618</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0309" target="_blank" rel="noreferrer">309</a>.</p>',6),o=[n];function s(c,d,l,h,p,f){return t(),r("div",null,o)}const v=e(a,[["render",s]]);export{u as __pageData,v as default};
