import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-4406 Powers and duties of the Executive Director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-4406.md","filePath":"vacode/54.1-4406.md"}'),i={name:"vacode/54.1-4406.md"},r=o('<h1 id="§-54-1-4406-powers-and-duties-of-the-executive-director" tabindex="-1">§ 54.1-4406 Powers and duties of the Executive Director. <a class="header-anchor" href="#§-54-1-4406-powers-and-duties-of-the-executive-director" aria-label="Permalink to &quot;§ 54.1-4406 Powers and duties of the Executive Director.&quot;">​</a></h1><p>Within the parameters of policies and guidelines established by the Board, the Executive Director shall have the power and duty to:</p><ol><li><p>Employ personnel and assistance necessary for the operation of the Board and the purposes of this chapter;</p></li><li><p>Make and enter into all contracts and agreements necessary or incidental to the performance of the duties of the Board and the execution of its powers under this chapter, including, but not limited to, contracts with the United States government, with agencies and governmental subdivisions of the Commonwealth, and with other states;</p></li><li><p>Accept grants from the United States government, its agencies and instrumentalities and any other source, and to these ends, the Board shall have the power to comply with conditions and execute agreements that are necessary, convenient, or desirable;</p></li><li><p>Serve as the secretary of the Board;</p></li><li><p>Maintain all records of the Board;</p></li><li><p>Collect and account for all fees and deposit them into the Board of Accountancy Fund, from which the expenses of the Board shall be paid;</p></li><li><p>Enforce all statutes and regulations the Executive Director is required to administer;</p></li><li><p>Exercise other powers necessary to function as the sole administrative officer of the Board; and</p></li><li><p>Perform any additional administrative functions prescribed by the Board.</p></li></ol><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>.</p>',4),n=[r];function s(d,c,l,h,p,f){return t(),a("div",null,n)}const m=e(i,[["render",s]]);export{_ as __pageData,m as default};
