import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-4409.1 Licensing requirements for persons.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-4409.1.md","filePath":"vacode/54.1-4409.1.md"}'),n={name:"vacode/54.1-4409.1.md"},a=t('<h1 id="§-54-1-4409-1-licensing-requirements-for-persons" tabindex="-1">§ 54.1-4409.1 Licensing requirements for persons. <a class="header-anchor" href="#§-54-1-4409-1-licensing-requirements-for-persons" aria-label="Permalink to &quot;§ 54.1-4409.1 Licensing requirements for persons.&quot;">​</a></h1><p>A. A person must be licensed in order to use the CPA title in Virginia.</p><ol><li><p>The person shall hold a Virginia license if he provides services to the public and the principal place of business in which he provides those services is in Virginia.</p></li><li><p>Other persons shall not be required to hold a Virginia license in order to use the CPA title in Virginia provided that they hold the license of another state and comply with the substantial equivalency provisions of § <a href="http://law.lis.virginia.gov/vacode/54.1-4411/" target="_blank" rel="noreferrer">54.1-4411</a>.</p></li></ol><p>B. The Board shall prescribe the methods, fees, and continuing professional education requirements for a person to apply for the issuance, renewal, or reinstatement of a Virginia license.</p><p>C. The Board has the authority to refuse to grant a person the privilege of using the CPA title in Virginia if, based upon all the information available, the Board finds that the person is unfit or unsuited to use the CPA title in Virginia. The Board shall not refuse to grant a person the privilege of using the CPA title in Virginia solely because of a criminal conviction.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0403" target="_blank" rel="noreferrer">403</a>.</p>',6),o=[a];function s(l,h,p,c,d,f){return i(),r("div",null,o)}const _=e(n,[["render",s]]);export{g as __pageData,_ as default};
