import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-4413.3 Standards of conduct and practice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-4413.3.md","filePath":"vacode/54.1-4413.3.md"}'),n={name:"vacode/54.1-4413.3.md"},r=a('<h1 id="§-54-1-4413-3-standards-of-conduct-and-practice" tabindex="-1">§ 54.1-4413.3 Standards of conduct and practice. <a class="header-anchor" href="#§-54-1-4413-3-standards-of-conduct-and-practice" aria-label="Permalink to &quot;§ 54.1-4413.3 Standards of conduct and practice.&quot;">​</a></h1><p>Persons using the CPA title in Virginia and firms providing attest services, compilation services, or financial statement preparation services to persons or entities located in Virginia shall conform to the following standards of conduct and practice.</p><ol><li><p>Exercise sensitive professional and moral judgment in all activities.</p></li><li><p>Act in a way that serves the public interest, honors the public trust, and demonstrates commitment to professionalism.</p></li><li><p>Perform all professional responsibilities with the highest sense of integrity, maintain objectivity and freedom from conflicts of interest in discharging professional responsibilities, and avoid knowingly misrepresenting facts or inappropriately subordinating judgment to others.</p></li><li><p>Follow the Code of Professional Conduct, and the related interpretive guidance, issued by the American Institute of Certified Public Accountants, or any successor standard-setting authorities.</p></li><li><p>Follow the technical standards, and the related interpretive guidance, issued by committees and boards of the American Institute of Certified Public Accountants that are designated by the Council of the American Institute of Certified Public Accountants to promulgate technical standards, or that are issued by any successor standard-setting authorities.</p></li><li><p>Follow the standards, and the related interpretive guidance, as applicable under the circumstances, issued by the Comptroller General of the United States, the Federal Accounting Standards Advisory Board, the Financial Accounting Standards Board, the Governmental Accounting Standards Board, the Public Company Accounting Oversight Board, the U. S. Securities and Exchange Commission, comparable international standard-setting authorities, or any successor standard-setting authorities.</p></li><li><p>Do not engage in any activity that is false, misleading, or deceptive.</p></li></ol><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0287" target="_blank" rel="noreferrer">287</a>.</p>',4),s=[r];function o(c,d,l,p,h,u){return t(),i("div",null,s)}const m=e(n,[["render",o]]);export{g as __pageData,m as default};
