import{_ as e,o as n,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-4413.5 Confidential consent agreements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-4413.5.md","filePath":"vacode/54.1-4413.5.md"}'),o={name:"vacode/54.1-4413.5.md"},a=t('<h1 id="§-54-1-4413-5-confidential-consent-agreements" tabindex="-1">§ 54.1-4413.5 Confidential consent agreements. <a class="header-anchor" href="#§-54-1-4413-5-confidential-consent-agreements" aria-label="Permalink to &quot;§ 54.1-4413.5 Confidential consent agreements.&quot;">​</a></h1><p>A. The Board may enter into a confidential consent agreement with a person or firm in lieu of disciplinary action.</p><p>B. A confidential consent agreement:</p><ol><li><p>Shall be entered into only in cases involving minor violations of the provisions of this chapter or regulations promulgated by the Board;</p></li><li><p>Shall not be disclosed by the person or firm;</p></li><li><p>Shall include findings of fact and may include an admission or a finding of a violation; and</p></li><li><p>Shall not be considered a notice or order of the Board but may be considered by the Board in future disciplinary proceedings.</p></li></ol><p>C. The Board shall adopt regulations in accordance with the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) to implement the provisions of this section. Such regulations shall identify the type of minor violations for which confidential consent orders may be offered and limit the number of confidential consent orders that may be offered to the same licensee in any given period. The Board shall not enter into a confidential consent agreement if there is probable cause to believe a licensee has demonstrated gross negligence or intentional misconduct in the practice of public accounting.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>.</p>',6),r=[a];function s(l,c,d,f,h,p){return n(),i("div",null,r)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
