import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-517 Penalties for willful violations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-517.md","filePath":"vacode/54.1-517.md"}'),i={name:"vacode/54.1-517.md"},o=t('<h1 id="§-54-1-517-penalties-for-willful-violations" tabindex="-1">§ 54.1-517 Penalties for willful violations. <a class="header-anchor" href="#§-54-1-517-penalties-for-willful-violations" aria-label="Permalink to &quot;§ 54.1-517 Penalties for willful violations.&quot;">​</a></h1><p>Notwithstanding any other provision of law, any person who willfully violates any provision of this chapter or any regulation related to licensure or training adopted pursuant to this chapter shall be guilty of a Class 1 misdemeanor for the first two violations and a Class 6 felony for a third and each subsequent violation within a three-year period.</p><p>In addition, licensed asbestos contractors, asbestos supervisors, asbestos inspectors, asbestos management planners, asbestos project designers, asbestos project monitors, asbestos analytical laboratories and asbestos workers, lead contractors, lead inspectors, lead risk assessors, lead project designers, lead supervisors, lead workers, renovators, dust sampling technicians, renovation contractors, and accredited asbestos training programs, accredited lead training programs, accredited renovator training programs, training managers or principal instructors may be assessed a civil penalty by the Board of not more than $1,000 for an initial violation and $5,000 for each subsequent violation within a three-year period arising from a willful violation of standards established by the Environmental Protection Agency, Occupational Safety and Health Administration, Department of Labor and Industry, or the Divisions of Air Pollution Control and Waste Management of the Department of Environmental Quality in a three-year period.</p><p>1987, c. 579, § 54-145.10:11; 1988, cc. 765, 802; 1989, c. 397; 1990, c. 823; 1993, c. 660; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0185" target="_blank" rel="noreferrer">185</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0911" target="_blank" rel="noreferrer">911</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0180" target="_blank" rel="noreferrer">180</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0739" target="_blank" rel="noreferrer">739</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0819" target="_blank" rel="noreferrer">819</a>.</p>',4),n=[o];function s(l,c,d,p,f,g){return a(),r("div",null,n)}const u=e(i,[["render",s]]);export{v as __pageData,u as default};
