import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-601 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-601.md","filePath":"vacode/54.1-601.md"}'),t={name:"vacode/54.1-601.md"},i=r('<h1 id="§-54-1-601-exemptions" tabindex="-1">§ 54.1-601 Exemptions. <a class="header-anchor" href="#§-54-1-601-exemptions" aria-label="Permalink to &quot;§ 54.1-601 Exemptions.&quot;">​</a></h1><p>The provisions of this chapter and the terms &quot;Virginia licensed auctioneer,&quot; &quot;auctioneer&quot; or &quot;auction firm,&quot; as defined in § <a href="/vacode/54.1-600/">54.1-600</a>, shall not apply to:</p><ol><li><p>Any person who auctions his own property, whether owned or leased, provided his regular business is not as an auctioneer;</p></li><li><p>Any person who is acting as a receiver, trustee in bankruptcy, guardian, conservator, administrator, or executor, or any person acting under order of a court;</p></li><li><p>A trustee acting under a trust agreement, deed of trust, or will;</p></li><li><p>An attorney-at-law licensed to practice in the Commonwealth of Virginia acting pursuant to a power of attorney;</p></li><li><p>Sales at auction conducted by or under the direction of any public authority, or pursuant to any judicial order or decree;</p></li><li><p>Sale of livestock at a public livestock market authorized by the Commissioner of Agriculture and Consumer Services;</p></li><li><p>Leaf tobacco sales conducted in accordance with the provisions of former § 3.1-336;</p></li><li><p>Sale at auction of automobiles conducted under the provisions of § <a href="/vacode/46.2-644.03/">46.2-644.03</a> or by a motor vehicle dealer licensed under the provisions of Chapter 15 (§ <a href="/vacode/46.2-1500/">46.2-1500</a> et seq.) of Title 46.2;</p></li><li><p>Sale at auction of a particular brand of livestock conducted by an auctioneer of a livestock trade association;</p></li><li><p>Sales conducted by and on behalf of any charitable, religious, civic club, fraternal, or political organization if the person conducting the sale receives no compensation, either directly or indirectly, therefor and has no ownership interest in the merchandise being sold or financial interest in the entity providing such merchandise;</p></li><li><p>Sales, not exceeding one sale per year, conducted by or on behalf of (i) a civic club or (ii) a charitable organization granted tax-exempt status under § 501(c)(3) of the Internal Revenue Code; or</p></li><li><p>Sales of collateral, sales conducted to enforce carriers&#39; or warehousemen&#39;s liens, bulk sales, sales of goods by a presenting bank following dishonor of a documentary draft, resales of rightfully rejected goods, resales of goods by an aggrieved seller, or other resales conducted pursuant to Titles 8.1A through 8.10 and the Virginia Self-Service Storage Act (§ <a href="/vacode/55.1-2900/">55.1-2900</a> et seq.).</p></li></ol><p>1982, c. 538, § 54-824.3; 1983, cc. 261, 522; 1986, c. 61; 1988, c. 765; 1991, c. 299; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0227" target="_blank" rel="noreferrer">227</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0021" target="_blank" rel="noreferrer">21</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>.</p>',4),n=[i];function l(c,s,d,p,u,h){return o(),a("div",null,n)}const v=e(t,[["render",l]]);export{g as __pageData,v as default};
