import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-602 Auctioneers Board; membership, meetings and powers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-602.md","filePath":"vacode/54.1-602.md"}'),o={name:"vacode/54.1-602.md"},s=i('<h1 id="§-54-1-602-auctioneers-board-membership-meetings-and-powers" tabindex="-1">§ 54.1-602 Auctioneers Board; membership, meetings and powers. <a class="header-anchor" href="#§-54-1-602-auctioneers-board-membership-meetings-and-powers" aria-label="Permalink to &quot;§ 54.1-602 Auctioneers Board; membership, meetings and powers.&quot;">​</a></h1><p>A. The Auctioneers Board shall be composed of five members as follows: three shall be Virginia licensed auctioneers and two shall be citizen members. Board members shall serve four-year terms.</p><p>The Board shall meet at least once each year for the purpose of transacting business. Special meetings of the Board may be held at the discretion of the Director.</p><p>B. The Board shall have the following authority and responsibilities:</p><ol><li><p>Establish regulations to obtain and retain licensure of auctioneers.</p></li><li><p>Make all case decisions regarding eligibility for initial licensure and renewal thereof.</p></li><li><p>To fine, suspend, deny renewal or revoke for cause, as defined in regulation, any license.</p></li><li><p>To examine auctioneers for licensure.</p></li></ol><p>1982, c. 538, §§ 54-824.4, 54-824.7; 1983, c. 522, § 54-824.9:3; 1988, cc. 42, 765; 1991, c. 299; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0522" target="_blank" rel="noreferrer">522</a>.</p>',6),t=[s];function n(l,c,d,p,h,m){return a(),r("div",null,t)}const u=e(o,[["render",n]]);export{f as __pageData,u as default};
