import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-701 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-701.md","filePath":"vacode/54.1-701.md"}'),a={name:"vacode/54.1-701.md"},n=t('<h1 id="§-54-1-701-exemptions" tabindex="-1">§ 54.1-701 Exemptions. <a class="header-anchor" href="#§-54-1-701-exemptions" aria-label="Permalink to &quot;§ 54.1-701 Exemptions.&quot;">​</a></h1><p>The provisions of this chapter shall not apply to:</p><ol><li><p>Persons authorized by the laws of the Commonwealth to practice medicine and surgery or osteopathy or chiropractic;</p></li><li><p>Registered nurses licensed to practice in the Commonwealth;</p></li><li><p>Persons employed in state or local penal or correctional institutions, rehabilitation centers, sanatoria, or institutions for care and treatment of individuals with mental illness or intellectual disability, or for care and treatment of geriatric patients, as barbers, cosmetologists, wax technicians, nail technicians, estheticians, barber instructors, cosmetology instructors, wax technician instructors, nail technician instructors, or esthetics instructors who practice only on inmates of or patients in such sanatoria or institutions;</p></li><li><p>Persons licensed as funeral directors or embalmers in the Commonwealth;</p></li><li><p>Gratuitous services as a barber, nail technician, cosmetologist, wax technician, tattooer, body-piercer, or esthetician;</p></li><li><p>Students enrolled in an approved school taking a course in barbering, nail care, cosmetology, waxing, tattooing, body-piercing, or esthetics;</p></li><li><p>Persons working in a cosmetology salon whose duties are expressly confined to the blow drying, arranging, dressing, curling, or cleansing of human hair;</p></li><li><p>Apprentices serving in a barbershop, nail salon, waxing salon, cosmetology salon, or esthetics spa licensed by the Board in accordance with the Board&#39;s regulations;</p></li><li><p>Schools of barbering, nail care, waxing, or cosmetology in public schools; and</p></li><li><p>Persons whose activities are confined solely to applying make-up, including such activities that are ancillary to applying make-up.</p></li></ol><p>1962, c. 639, § 4, § 54-83.5; 1968, c. 622; 1988, c. 765; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0726" target="_blank" rel="noreferrer">726</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0600" target="_blank" rel="noreferrer">600</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0404" target="_blank" rel="noreferrer">404</a>.</p>',4),o=[n];function l(s,c,p,g,h,f){return i(),r("div",null,o)}const b=e(a,[["render",l]]);export{u as __pageData,b as default};
